option.value:
'use client'

import { useEffect, useState } from 'react'
import { useParams, useRouter } from 'next/navigation'
import { Button } from '@/components/ui/button'
import { Card, CardContent, CardHeader, CardTitle, CardDescription } from '@/components/ui/card'
import { Badge } from '@/components/ui/badge'
import { Label } from '@/components/ui/label'
import { Textarea } from '@/components/ui/textarea'
import { ArrowLeft, CheckCircle, XCircle, User, Building, MapPin, Phone, Mail, Calendar } from 'lucide-react'
import { api } from '@/lib/api'
import { useToast } from '@/hooks/use-toast'
import {
  Dialog,
  DialogContent,
  DialogDescription,
  DialogFooter,
  DialogHeader,
  DialogTitle,
} from '@/components/ui/dialog'

interface Application {
  id: string
  userId: string
  businessName: string
  businessDescription: string
  businessAddress: string
  businessPhone: string
  businessEmail: string
  category: string
  additionalNotes?: string
  status: string
  applicationDate: string
  reviewedBy?: string
  reviewedAt?: string
  rejectionReason?: string
  user: {
    firstName: string
    lastName: string
  }
}

export default function ApplicationDetailPage() {
  const params = useParams()
  const router = useRouter()
  const { toast } = useToast()
  const [application, setApplication] = useState<Application | null>(null)
  const [loading, setLoading] = useState(true)
  const [actionLoading, setActionLoading] = useState(false)
  const [rejectDialogOpen, setRejectDialogOpen] = useState(false)
  const [rejectionReason, setRejectionReason] = useState('')

  useEffect(() => {
    fetchApplication()
  }, [params.id])

  const fetchApplication = async () => {
    setLoading(true)
    try {
      const data = await api.get<Application>(`/admin/applications/${params.id}`)
      setApplication(data)
    } catch (error: any) {
      console.error('Error fetching application:', error)
      toast({
        title: 'Error',
        description: error.message || 'Failed to load application',
        variant: 'destructive',
      })
    } finally {
      setLoading(false)
    }
  }

  const handleApprove = async () => {
    if (!confirm('Are you sure you want to approve this application? This will create a restaurant and grant the user Restaurant Owner role.')) return

    setActionLoading(true)
    try {
      await api.post(`/admin/applications/${params.id}/approve`, {})
      toast({
        title: 'Success',
        description: 'Application approved successfully',
      })
      router.push('/admin/applications')
    } catch (error: any) {
      console.error('Error approving application:', error)
      toast({
        title: 'Error',
        description: error.message || 'Failed to approve application',
        variant: 'destructive',
      })
    } finally {
      setActionLoading(false)
    }
  }

  const handleReject = async () => {
    if (!rejectionReason.trim()) {
      toast({
        title: 'Error',
        description: 'Please provide a rejection reason',
        variant: 'destructive',
      })
      return
    }

    setActionLoading(true)
    try {
      await api.post(`/admin/applications/${params.id}/reject`, { reason: rejectionReason })
      toast({
        title: 'Success',
        description: 'Application rejected successfully',
      })
      setRejectDialogOpen(false)
      router.push('/admin/applications')
    } catch (error: any) {
      console.error('Error rejecting application:', error)
      toast({
        title: 'Error',
        description: error.message || 'Failed to reject application',
        variant: 'destructive',
      })
    } finally {
      setActionLoading(false)
    }
  }

  const getStatusBadge = (status: string) => {
    switch (status.toLowerCase()) {
      case 'pending':
        return <Badge variant="outline" className="bg-yellow-100">Pending</Badge>
      case 'approved':
        return <Badge variant="default" className="bg-green-600">Approved</Badge>
      case 'rejected':
        return <Badge variant="destructive">Rejected</Badge>
      default:
        return <Badge variant="outline">{status}</Badge>
    }
  }

  if (loading) {
    return (
      <div className="container mx-auto p-6">
        <div className="text-center py-8">Loading application details...</div>
      </div>
    )
  }

  if (!application) {
    return (
      <div className="container mx-auto p-6">
        <Card>
          <CardHeader>
            <CardTitle>Application Not Found</CardTitle>
          </CardHeader>
        </Card>
      </div>
    )
  }

  return (
    <div className="container mx-auto p-6 max-w-4xl">
      <Button
        variant="ghost"
        onClick={() => router.push('/admin/applications')}
        className="mb-4"
      >
        <ArrowLeft className="w-4 h-4 mr-2" />
        Back to Applications
      </Button>

      <div className="space-y-6">
        {/* Header Card */}
        <Card>
          <CardHeader>
            <div className="flex items-center justify-between">
              <div>
                <CardTitle className="text-2xl">Restaurant Ownership Application</CardTitle>
                <CardDescription>Application ID: {application.id}</CardDescription>
              </div>
              {getStatusBadge(application.status)}
            </div>
          </CardHeader>
        </Card>

        {/* Applicant Information */}
        <Card>
          <CardHeader>
            <CardTitle className="flex items-center gap-2">
              <User className="w-5 h-5" />
              Applicant Information
            </CardTitle>
          </CardHeader>
          <CardContent className="space-y-3">
            <div className="grid grid-cols-2 gap-4">
              <div>
                <Label className="text-muted-foreground">Name</Label>
                <p className="font-medium">{application.user.firstName} {application.user.lastName}</p>
              </div>
              <div>
                <Label className="text-muted-foreground">Application Date</Label>
                <p className="font-medium flex items-center gap-1">
                  <Calendar className="w-4 h-4" />
                  {new Date(application.applicationDate).toLocaleDateString()}
                </p>
              </div>
            </div>
          </CardContent>
        </Card>

        {/* Business Information */}
        <Card>
          <CardHeader>
            <CardTitle className="flex items-center gap-2">
              <Building className="w-5 h-5" />
              Business Information
            </CardTitle>
          </CardHeader>
          <CardContent className="space-y-4">
            <div>
              <Label className="text-muted-foreground">Business Name</Label>
              <p className="font-medium text-lg">{application.businessName}</p>
            </div>
            <div>
              <Label className="text-muted-foreground">Category</Label>
              <div className="mt-1">
                <Badge variant="outline">{application.category}</Badge>
              </div>
            </div>
            <div>
              <Label className="text-muted-foreground">Description</Label>
              <p className="text-sm">{application.businessDescription}</p>
            </div>
            <div className="grid grid-cols-2 gap-4">
              <div>
                <Label className="text-muted-foreground flex items-center gap-1">
                  <MapPin className="w-4 h-4" />
                  Address
                </Label>
                <p className="text-sm">{application.businessAddress}</p>
              </div>
              <div>
                <Label className="text-muted-foreground flex items-center gap-1">
                  <Phone className="w-4 h-4" />
                  Phone
                </Label>
                <p className="text-sm">{application.businessPhone}</p>
              </div>
            </div>
            {application.businessEmail && (
              <div>
                <Label className="text-muted-foreground flex items-center gap-1">
                  <Mail className="w-4 h-4" />
                  Email
                </Label>
                <p className="text-sm">{application.businessEmail}</p>
              </div>
            )}
            {application.additionalNotes && (
              <div>
                <Label className="text-muted-foreground">Additional Notes</Label>
                <p className="text-sm">{application.additionalNotes}</p>
              </div>
            )}
          </CardContent>
        </Card>

        {/* Review Information */}
        {(application.reviewedAt || application.rejectionReason) && (
          <Card>
            <CardHeader>
              <CardTitle>Review Information</CardTitle>
            </CardHeader>
            <CardContent className="space-y-3">
              {application.reviewedAt && (
                <div>
                  <Label className="text-muted-foreground">Reviewed At</Label>
                  <p className="text-sm">{new Date(application.reviewedAt).toLocaleString()}</p>
                </div>
              )}
              {application.rejectionReason && (
                <div>
                  <Label className="text-muted-foreground">Rejection Reason</Label>
                  <p className="text-sm">{application.rejectionReason}</p>
                </div>
              )}
            </CardContent>
          </Card>
        )}

        {/* Actions */}
        {application.status.toLowerCase() === 'pending' && (
          <Card>
            <CardHeader>
              <CardTitle>Actions</CardTitle>
              <CardDescription>Review and make a decision on this application</CardDescription>
            </CardHeader>
            <CardContent>
              <div className="flex gap-4">
                <Button
                  onClick={handleApprove}
                  disabled={actionLoading}
                  className="flex-1"
                >
                  <CheckCircle className="w-4 h-4 mr-2" />
                  Approve Application
                </Button>
                <Button
                  variant="destructive"
                  onClick={() => setRejectDialogOpen(true)}
                  disabled={actionLoading}
                  className="flex-1"
                >
                  <XCircle className="w-4 h-4 mr-2" />
                  Reject Application
                </Button>
              </div>
            </CardContent>
          </Card>
        )}
      </div>

      {/* Reject Dialog */}
      <Dialog open={rejectDialogOpen} onOpenChange={setRejectDialogOpen}>
        <DialogContent>
          <DialogHeader>
            <DialogTitle>Reject Application</DialogTitle>
            <DialogDescription>
              Please provide a reason for rejecting this application. This will be visible to the applicant.
            </DialogDescription>
          </DialogHeader>
          <div className="py-4">
            <Textarea
              placeholder="Enter rejection reason..."
              value={rejectionReason}
              onChange={(e) => setRejectionReason(e.target.value)}
              rows={4}
            />
          </div>
          <DialogFooter>
            <Button
              variant="outline"
              onClick={() => setRejectDialogOpen(false)}
              disabled={actionLoading}
            >
              Cancel
            </Button>
            <Button
              variant="destructive"
              onClick={handleReject}
              disabled={actionLoading || !rejectionReason.trim()}
            >
              {actionLoading ? 'Rejecting...' : 'Reject Application'}
            </Button>
          </DialogFooter>
        </DialogContent>
      </Dialog>
    </div>
  )
}

'use client'

import { useEffect, useState } from 'react'
import { Button } from '@/components/ui/button'
import { Card, CardContent, CardHeader, CardTitle, CardDescription } from '@/components/ui/card'
import { Input } from '@/components/ui/input'
import { Label } from '@/components/ui/label'
import { Textarea } from '@/components/ui/textarea'
import { Badge } from '@/components/ui/badge'
import { useToast } from '@/hooks/use-toast'
import { loyaltyApi } from '@/lib/api'
import { Gift, Copy, CheckCircle, XCircle } from 'lucide-react'
import {
  Dialog,
  DialogContent,
  DialogDescription,
  DialogFooter,
  DialogHeader,
  DialogTitle,
} from '@/components/ui/dialog'

interface LoyaltyCode {
  id: string
  code: string
  pointValue: number
  description?: string
  isActive: boolean
  maxUses?: number
  currentUses: number
  createdAt: string
  expiryDate?: string
  isUsed: boolean
}

const POINT_PRESETS = [200, 500, 1000, 2000, 5000]

export default function AdminLoyaltyPage() {
  const { toast } = useToast()
  const [codes, setCodes] = useState<LoyaltyCode[]>([])
  const [loading, setLoading] = useState(false)
  const [dialogOpen, setDialogOpen] = useState(false)
  const [generatedCode, setGeneratedCode] = useState<any>(null)

  const [formData, setFormData] = useState({
    pointValue: 500,
    description: '',
    maxUses: 1,
    expiryDate: '',
  })

  useEffect(() => {
    fetchCodes()
  }, [])

  const fetchCodes = async () => {
    setLoading(true)
    try {
      const data = await loyaltyApi.admin.getAllCodes()
      setCodes(data)
    } catch (error: any) {
      toast({
        title: 'Error',
        description: error.message,
        variant: 'destructive',
      })
    } finally {
      setLoading(false)
    }
  }

  const handleGenerateCode = async () => {
    if (!formData.pointValue || formData.pointValue < 1) {
      toast({
        title: 'Error',
        description: 'Please enter a valid point value',
        variant: 'destructive',
      })
      return
    }

    setLoading(true)
    try {
      const result = await loyaltyApi.admin.generateCode({
        pointValue: formData.pointValue,
        description: formData.description || undefined,
        maxUses: formData.maxUses || undefined,
        expiryDate: formData.expiryDate || undefined,
      })

      setGeneratedCode(result)
      setDialogOpen(true)
      fetchCodes()

      // Reset form
      setFormData({
        pointValue: 500,
        description: '',
        maxUses: 1,
        expiryDate: '',
      })
    } catch (error: any) {
      toast({
        title: 'Error',
        description: error.message,
        variant: 'destructive',
      })
    } finally {
      setLoading(false)
    }
  }

  const handleDeactivate = async (codeId: string) => {
    if (!confirm('Are you sure you want to deactivate this code?')) return

    try {
      await loyaltyApi.admin.deactivateCode(codeId)
      toast({
        title: 'Success',
        description: 'Code deactivated successfully',
      })
      fetchCodes()
    } catch (error: any) {
      toast({
        title: 'Error',
        description: error.message,
        variant: 'destructive',
      })
    }
  }

  const copyToClipboard = (text: string) => {
    navigator.clipboard.writeText(text)
    toast({
      title: 'Copied!',
      description: 'Code copied to clipboard',
    })
  }

  return (
    <div className="container mx-auto p-6 max-w-6xl">
      <div className="mb-6">
        <h1 className="text-3xl font-bold">Loyalty Point Management</h1>
        <p className="text-muted-foreground">Generate and manage loyalty point codes</p>
      </div>

      {/* Generate Code Form */}
      <Card className="mb-6">
        <CardHeader>
          <CardTitle className="flex items-center gap-2">
            <Gift className="w-5 h-5" />
            Generate New Loyalty Code
          </CardTitle>
          <CardDescription>Create a new code that customers can redeem for loyalty points</CardDescription>
        </CardHeader>
        <CardContent>
          <div className="space-y-4">
            <div>
              <Label>Point Value</Label>
              <div className="flex gap-2 mt-2">
                {POINT_PRESETS.map((preset) => (
                  <Button
                    key={preset}
                    variant={formData.pointValue === preset ? 'default' : 'outline'}
                    onClick={() => setFormData({ ...formData, pointValue: preset })}
                  >
                    {preset}
                  </Button>
                ))}
              </div>
              <Input
                type="number"
                value={formData.pointValue}
                onChange={(e) => setFormData({ ...formData, pointValue: parseInt(e.target.value) || 0 })}
                className="mt-2"
                placeholder="Or enter custom value"
              />
            </div>

            <div>
              <Label>Description (Optional)</Label>
              <Textarea
                value={formData.description}
                onChange={(e) => setFormData({ ...formData, description: e.target.value })}
                placeholder="e.g., New Year Special, Summer Bonus"
                rows={2}
              />
            </div>

            <div className="grid grid-cols-2 gap-4">
              <div>
                <Label>Max Uses (Optional)</Label>
                <Input
                  type="number"
                  value={formData.maxUses}
                  onChange={(e) => setFormData({ ...formData, maxUses: parseInt(e.target.value) || undefined })}
                  placeholder="Leave empty for unlimited"
                />
              </div>
              <div>
                <Label>Expiry Date (Optional)</Label>
                <Input
                  type="datetime-local"
                  value={formData.expiryDate}
                  onChange={(e) => setFormData({ ...formData, expiryDate: e.target.value })}
                />
              </div>
            </div>

            <Button onClick={handleGenerateCode} disabled={loading} className="w-full">
              {loading ? 'Generating...' : 'Generate Code'}
            </Button>
          </div>
        </CardContent>
      </Card>

      {/* Codes List */}
      <Card>
        <CardHeader>
          <CardTitle>Generated Codes</CardTitle>
          <CardDescription>All loyalty codes created by admin</CardDescription>
        </CardHeader>
        <CardContent>
          {loading && codes.length === 0 ? (
            <div className="text-center py-8 text-muted-foreground">Loading...</div>
          ) : codes.length === 0 ? (
            <div className="text-center py-8 text-muted-foreground">No codes generated yet</div>
          ) : (
            <div className="space-y-3">
              {codes.map((code) => (
                <div key={code.id} className="border rounded-lg p-4">
                  <div className="flex items-center justify-between">
                    <div className="flex-1">
                      <div className="flex items-center gap-3 mb-2">
                        <code className="text-lg font-mono font-bold bg-muted px-3 py-1 rounded">
                          {code.code}
                        </code>
                        <Button
                          variant="ghost"
                          size="sm"
                          onClick={() => copyToClipboard(code.code)}
                        >
                          <Copy className="w-4 h-4" />
                        </Button>
                        <Badge variant={code.isActive ? 'default' : 'secondary'}>
                          {code.isActive ? 'Active' : 'Inactive'}
                        </Badge>
                        <Badge variant="outline">{code.pointValue} Points</Badge>
                      </div>
                      {code.description && (
                        <p className="text-sm text-muted-foreground mb-2">{code.description}</p>
                      )}
                      <div className="flex gap-4 text-sm text-muted-foreground">
                        <span>Uses: {code.currentUses}{code.maxUses ? `/${code.maxUses}` : ''}</span>
                        <span>Created: {new Date(code.createdAt).toLocaleDateString()}</span>
                        {code.expiryDate && (
                          <span>Expires: {new Date(code.expiryDate).toLocaleDateString()}</span>
                        )}
                      </div>
                    </div>
                    {code.isActive && (
                      <Button
                        variant="destructive"
                        size="sm"
                        onClick={() => handleDeactivate(code.id)}
                      >
                        <XCircle className="w-4 h-4 mr-2" />
                        Deactivate
                      </Button>
                    )}
                  </div>
                </div>
              ))}
            </div>
          )}
        </CardContent>
      </Card>

      {/* Success Dialog */}
      <Dialog open={dialogOpen} onOpenChange={setDialogOpen}>
        <DialogContent>
          <DialogHeader>
            <DialogTitle className="flex items-center gap-2">
              <CheckCircle className="w-5 h-5 text-green-600" />
              Code Generated Successfully!
            </DialogTitle>
            <DialogDescription>
              Share this code with customers to let them redeem loyalty points
            </DialogDescription>
          </DialogHeader>
          {generatedCode && (
            <div className="py-4">
              <div className="bg-muted p-4 rounded-lg text-center">
                <code className="text-2xl font-mono font-bold">{generatedCode.code}</code>
                <Button
                  variant="ghost"
                  size="sm"
                  onClick={() => copyToClipboard(generatedCode.code)}
                  className="ml-2"
                >
                  <Copy className="w-4 h-4" />
                </Button>
              </div>
              <div className="mt-4 space-y-2 text-sm">
                <p><strong>Point Value:</strong> {generatedCode.pointValue}</p>
                {generatedCode.description && (
                  <p><strong>Description:</strong> {generatedCode.description}</p>
                )}
                {generatedCode.expiryDate && (
                  <p><strong>Expires:</strong> {new Date(generatedCode.expiryDate).toLocaleDateString()}</p>
                )}
              </div>
            </div>
          )}
          <DialogFooter>
            <Button onClick={() => setDialogOpen(false)}>Close</Button>
          </DialogFooter>
        </DialogContent>
      </Dialog>
    </div>
  )
}

'use client'

import { useEffect, useState } from 'react'
import { customerApi } from '@/lib/customer-api'
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '@/components/ui/card'
import { Button } from '@/components/ui/button'
import { Badge } from '@/components/ui/badge'
import { Award, Gift, Star, TrendingUp, Check } from 'lucide-react'
import { useToast } from '@/hooks/use-toast'
import { Tabs, TabsContent, TabsList, TabsTrigger } from '@/components/ui/tabs'

export default function CustomerLoyaltyPage() {
  const [restaurants, setRestaurants] = useState<any[]>([])
  const [selectedRestaurant, setSelectedRestaurant] = useState<string | null>(null)
  const [loyaltyPoints, setLoyaltyPoints] = useState<number>(0)
  const [rewards, setRewards] = useState<any[]>([])
  const [loading, setLoading] = useState(true)
  const { toast } = useToast()

  useEffect(() => {
    loadRestaurants()
  }, [])

  useEffect(() => {
    if (selectedRestaurant) {
      loadLoyaltyData(selectedRestaurant)
    }
  }, [selectedRestaurant])

  const loadRestaurants = async () => {
    try {
      const favorites = await customerApi.favorites.getAll()
      setRestaurants(favorites)
      if (favorites.length > 0) {
        setSelectedRestaurant(favorites[0].id)
      }
    } catch (error) {
      console.error('Error loading restaurants:', error)
    } finally {
      setLoading(false)
    }
  }

  const loadLoyaltyData = async (restaurantId: string) => {
    try {
      const [pointsData, rewardsData] = await Promise.all([
        customerApi.loyalty.getPoints(restaurantId),
        customerApi.loyalty.getRewards(restaurantId),
      ])
      setLoyaltyPoints(pointsData.points)
      setRewards(rewardsData)
    } catch (error) {
      console.error('Error loading loyalty data:', error)
      toast({
        title: 'Hata',
        description: 'Sadakat verileri yüklenirken bir hata oluştu',
        variant: 'destructive',
      })
    }
  }

  const handleRedeemReward = async (rewardId: string) => {
    if (!confirm('Bu ödülü kullanmak istediğinize emin misiniz?')) return

    try {
      await customerApi.loyalty.redeemReward(rewardId)
      toast({
        title: 'Başarılı!',
        description: 'Ödül başarıyla kullanıldı',
      })
      if (selectedRestaurant) {
        loadLoyaltyData(selectedRestaurant)
      }
    } catch (error: any) {
      toast({
        title: 'Hata',
        description: error.message || 'Ödül kullanılırken bir hata oluştu',
        variant: 'destructive',
      })
    }
  }

  if (loading) {
    return (
      <div className="flex items-center justify-center min-h-screen">
        <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-gray-900"></div>
      </div>
    )
  }

  if (restaurants.length === 0) {
    return (
      <div className="container mx-auto p-6">
        <Card>
          <CardContent className="py-12 text-center">
            <Award className="h-12 w-12 mx-auto text-muted-foreground mb-4" />
            <p className="text-muted-foreground mb-4">
              Sadakat programından faydalanmak için favori restoranlarınızı ekleyin
            </p>
            <Button asChild>
              <a href="/restaurants">Restoranları Keşfet</a>
            </Button>
          </CardContent>
        </Card>
      </div>
    )
  }

  const selectedRestaurantData = restaurants.find(r => r.id === selectedRestaurant)

  return (
    <div className="container mx-auto p-6 space-y-6">
      <div>
        <h1 className="text-3xl font-bold flex items-center gap-2">
          <Award className="h-8 w-8 text-yellow-500" />
          Sadakat Programı & Ödüller
        </h1>
        <p className="text-muted-foreground">Puanlarınızı biriktirin, ödüllerden yararlanın</p>
      </div>

      {/* Restaurant Selector */}
      <Card>
        <CardHeader>
          <CardTitle>Restoran Seçin</CardTitle>
          <CardDescription>Sadakat puanlarını ve ödüllerini görmek için bir restoran seçin</CardDescription>
        </CardHeader>
        <CardContent>
          <div className="flex flex-wrap gap-2">
            {restaurants.map((restaurant) => (
              <Button
                key={restaurant.id}
                variant={selectedRestaurant === restaurant.id ? 'default' : 'outline'}
                onClick={() => setSelectedRestaurant(restaurant.id)}
              >
                {restaurant.name}
              </Button>
            ))}
          </div>
        </CardContent>
      </Card>

      {selectedRestaurant && (
        <>
          {/* Loyalty Points Card */}
          <Card className="bg-gradient-to-r from-yellow-400 to-orange-500 text-white">
            <CardHeader>
              <CardTitle className="text-2xl flex items-center gap-2">
                <Star className="h-6 w-6 fill-white" />
                {selectedRestaurantData?.name}
              </CardTitle>
              <CardDescription className="text-white/90">
                Mevcut Sadakat Puanlarınız
              </CardDescription>
            </CardHeader>
            <CardContent>
              <div className="text-5xl font-bold">{loyaltyPoints}</div>
              <p className="text-white/90 mt-2">Puan</p>
            </CardContent>
          </Card>

          {/* Rewards Section */}
          <Card>
            <CardHeader>
              <CardTitle className="flex items-center gap-2">
                <Gift className="h-5 w-5" />
                Mevcut Ödüller
              </CardTitle>
              <CardDescription>
                Puanlarınızı kullanarak bu ödüllerden yararlanabilirsiniz
              </CardDescription>
            </CardHeader>
            <CardContent>
              {rewards.length === 0 ? (
                <div className="text-center py-8">
                  <Gift className="h-12 w-12 mx-auto text-muted-foreground mb-4" />
                  <p className="text-muted-foreground">
                    Bu restoran için henüz ödül bulunmuyor
                  </p>
                </div>
              ) : (
                <div className="grid gap-4 md:grid-cols-2">
                  {rewards.map((reward) => (
                    <Card key={reward.id} className={reward.canRedeem ? 'border-yellow-500' : ''}>
                      <CardHeader>
                        <div className="flex justify-between items-start">
                          <div>
                            <CardTitle className="text-lg">{reward.name}</CardTitle>
                            <CardDescription>{reward.description}</CardDescription>
                          </div>
                          {reward.canRedeem && (
                            <Badge className="bg-yellow-500">Kullanılabilir</Badge>
                          )}
                        </div>
                      </CardHeader>
                      <CardContent>
                        <div className="space-y-4">
                          {/* Reward Details */}
                          <div className="flex items-center justify-between">
                            <div className="flex items-center gap-2">
                              <TrendingUp className="h-4 w-4 text-muted-foreground" />
                              <span className="text-sm font-medium">
                                {reward.pointsRequired} Puan Gerekli
                              </span>
                            </div>
                            {reward.discountAmount && (
                              <Badge variant="secondary">
                                ₺{reward.discountAmount} İndirim
                              </Badge>
                            )}
                            {reward.discountPercentage && (
                              <Badge variant="secondary">
                                %{reward.discountPercentage} İndirim
                              </Badge>
                            )}
                          </div>

                          {/* Progress Bar */}
                          <div className="space-y-2">
                            <div className="w-full bg-gray-200 rounded-full h-2">
                              <div
                                className="bg-yellow-500 h-2 rounded-full transition-all"
                                style={{
                                  width: `${Math.min((loyaltyPoints / reward.pointsRequired) * 100, 100)}%`,
                                }}
                              />
                            </div>
                            <p className="text-xs text-muted-foreground text-center">
                              {loyaltyPoints} / {reward.pointsRequired} Puan
                            </p>
                          </div>

                          {/* Redeem Button */}
                          <Button
                            className="w-full"
                            disabled={!reward.canRedeem}
                            onClick={() => handleRedeemReward(reward.id)}
                          >
                            {reward.canRedeem ? (
                              <>
                                <Check className="h-4 w-4 mr-2" />
                                Ödülü Kullan
                              </>
                            ) : (
                              `${reward.pointsRequired - loyaltyPoints} Puan Daha Gerekli`
                            )}
                          </Button>

                          {/* Expiry Info */}
                          {reward.endDate && (
                            <p className="text-xs text-muted-foreground text-center">
                              Son Kullanma: {new Date(reward.endDate).toLocaleDateString('tr-TR')}
                            </p>
                          )}
                        </div>
                      </CardContent>
                    </Card>
                  ))}
                </div>
              )}
            </CardContent>
          </Card>

          {/* How it Works */}
          <Card>
            <CardHeader>
              <CardTitle>Nasıl Çalışır?</CardTitle>
            </CardHeader>
            <CardContent>
              <div className="space-y-3">
                <div className="flex items-start gap-3">
                  <div className="flex h-6 w-6 items-center justify-center rounded-full bg-yellow-500 text-white text-sm font-bold">
                    1
                  </div>
                  <div>
                    <p className="font-medium">Sipariş Verin</p>
                    <p className="text-sm text-muted-foreground">
                      Her siparişinizde otomatik olarak puan kazanırsınız
                    </p>
                  </div>
                </div>
                <div className="flex items-start gap-3">
                  <div className="flex h-6 w-6 items-center justify-center rounded-full bg-yellow-500 text-white text-sm font-bold">
                    2
                  </div>
                  <div>
                    <p className="font-medium">Puan Biriktirin</p>
                    <p className="text-sm text-muted-foreground">
                      Puanlarınız hesabınızda birikir
                    </p>
                  </div>
                </div>
                <div className="flex items-start gap-3">
                  <div className="flex h-6 w-6 items-center justify-center rounded-full bg-yellow-500 text-white text-sm font-bold">
                    3
                  </div>
                  <div>
                    <p className="font-medium">Ödül Kazanın</p>
                    <p className="text-sm text-muted-foreground">
                      Yeterli puan topladığınızda ödüllerden yararlanın
                    </p>
                  </div>
                </div>
              </div>
            </CardContent>
          </Card>
        </>
      )}
    </div>
  )
}

'use client'

import { useEffect, useState } from 'react'
import { Button } from '@/components/ui/button'
import { Card, CardContent, CardHeader, CardTitle, CardDescription } from '@/components/ui/card'
import { Input } from '@/components/ui/input'
import { Label } from '@/components/ui/label'
import { Textarea } from '@/components/ui/textarea'
import { Badge } from '@/components/ui/badge'
import { useToast } from '@/hooks/use-toast'
import { loyaltyApi } from '@/lib/api'
import { useAuth } from '@/contexts/auth-context'
import { Gift, Plus, Edit, Trash2 } from 'lucide-react'
import {
  Dialog,
  DialogContent,
  DialogDescription,
  DialogFooter,
  DialogHeader,
  DialogTitle,
} from '@/components/ui/dialog'
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from '@/components/ui/select'

interface Reward {
  id: string
  restaurantId: string
  restaurantName: string
  name: string
  description: string
  pointsRequired: number
  discountAmount?: number
  discountPercentage?: number
  imageUrl?: string
  isActive: boolean
  startDate?: string
  endDate?: string
  maxRedemptions?: number
  currentRedemptions: number
}

export default function OwnerRewardsPage() {
  const { toast } = useToast()
  const { user } = useAuth()
  const [rewards, setRewards] = useState<Reward[]>([])
  const [restaurants, setRestaurants] = useState<any[]>([])
  const [loading, setLoading] = useState(false)
  const [dialogOpen, setDialogOpen] = useState(false)
  const [editingReward, setEditingReward] = useState<Reward | null>(null)

  const [formData, setFormData] = useState({
    restaurantId: '',
    name: '',
    description: '',
    pointsRequired: 1000,
    discountAmount: '',
    discountPercentage: '',
    imageUrl: '',
    startDate: '',
    endDate: '',
    maxRedemptions: '',
  })

  useEffect(() => {
    fetchRestaurants()
  }, [])

  useEffect(() => {
    if (formData.restaurantId) {
      fetchRewards()
    }
  }, [formData.restaurantId])

  const fetchRestaurants = async () => {
    try {
      const data = await loyaltyApi.admin.getAllCodes() // Use owner API in real implementation
      // In real implementation, fetch owner's restaurants
      // For now, we'll set a placeholder
    } catch (error: any) {
      console.error(error)
    }
  }

  const fetchRewards = async () => {
    if (!formData.restaurantId) return

    setLoading(true)
    try {
      const data = await loyaltyApi.getRestaurantRewards(formData.restaurantId)
      setRewards(data)
    } catch (error: any) {
      toast({
        title: 'Error',
        description: error.message,
        variant: 'destructive',
      })
    } finally {
      setLoading(false)
    }
  }

  const handleSubmit = async () => {
    if (!formData.name || !formData.description || !formData.pointsRequired) {
      toast({
        title: 'Error',
        description: 'Please fill in all required fields',
        variant: 'destructive',
      })
      return
    }

    setLoading(true)
    try {
      const data = {
        restaurantId: formData.restaurantId,
        name: formData.name,
        description: formData.description,
        pointsRequired: formData.pointsRequired,
        discountAmount: formData.discountAmount ? parseFloat(formData.discountAmount) : undefined,
        discountPercentage: formData.discountPercentage ? parseInt(formData.discountPercentage) : undefined,
        imageUrl: formData.imageUrl || undefined,
        startDate: formData.startDate || undefined,
        endDate: formData.endDate || undefined,
        maxRedemptions: formData.maxRedemptions ? parseInt(formData.maxRedemptions) : undefined,
      }

      if (editingReward) {
        await loyaltyApi.owner.updateReward(editingReward.id, { ...data, isActive: editingReward.isActive })
        toast({
          title: 'Success',
          description: 'Reward updated successfully',
        })
      } else {
        await loyaltyApi.owner.createReward(data)
        toast({
          title: 'Success',
          description: 'Reward created successfully',
        })
      }

      setDialogOpen(false)
      resetForm()
      fetchRewards()
    } catch (error: any) {
      toast({
        title: 'Error',
        description: error.message,
        variant: 'destructive',
      })
    } finally {
      setLoading(false)
    }
  }

  const handleDelete = async (rewardId: string) => {
    if (!confirm('Are you sure you want to delete this reward?')) return

    try {
      await loyaltyApi.owner.deleteReward(rewardId)
      toast({
        title: 'Success',
        description: 'Reward deleted successfully',
      })
      fetchRewards()
    } catch (error: any) {
      toast({
        title: 'Error',
        description: error.message,
        variant: 'destructive',
      })
    }
  }

  const handleEdit = (reward: Reward) => {
    setEditingReward(reward)
    setFormData({
      restaurantId: reward.restaurantId,
      name: reward.name,
      description: reward.description,
      pointsRequired: reward.pointsRequired,
      discountAmount: reward.discountAmount?.toString() || '',
      discountPercentage: reward.discountPercentage?.toString() || '',
      imageUrl: reward.imageUrl || '',
      startDate: reward.startDate ? new Date(reward.startDate).toISOString().slice(0, 16) : '',
      endDate: reward.endDate ? new Date(reward.endDate).toISOString().slice(0, 16) : '',
      maxRedemptions: reward.maxRedemptions?.toString() || '',
    })
    setDialogOpen(true)
  }

  const resetForm = () => {
    setEditingReward(null)
    setFormData({
      ...formData,
      name: '',
      description: '',
      pointsRequired: 1000,
      discountAmount: '',
      discountPercentage: '',
      imageUrl: '',
      startDate: '',
      endDate: '',
      maxRedemptions: '',
    })
  }

  return (
    <div className="container mx-auto p-6 max-w-6xl">
      <div className="flex items-center justify-between mb-6">
        <div>
          <h1 className="text-3xl font-bold">Rewards Management</h1>
          <p className="text-muted-foreground">Create and manage loyalty rewards for your customers</p>
        </div>
        <Button onClick={() => setDialogOpen(true)}>
          <Plus className="w-4 h-4 mr-2" />
          Create Reward
        </Button>
      </div>

      {/* Restaurant Selector */}
      <Card className="mb-6">
        <CardHeader>
          <CardTitle>Select Restaurant</CardTitle>
        </CardHeader>
        <CardContent>
          <Input
            placeholder="Enter Restaurant ID"
            value={formData.restaurantId}
            onChange={(e) => setFormData({ ...formData, restaurantId: e.target.value })}
          />
          <p className="text-sm text-muted-foreground mt-2">
            Enter your restaurant ID to manage its rewards
          </p>
        </CardContent>
      </Card>

      {/* Rewards List */}
      {formData.restaurantId && (
        <Card>
          <CardHeader>
            <CardTitle>Rewards</CardTitle>
            <CardDescription>Manage your restaurant's loyalty rewards</CardDescription>
          </CardHeader>
          <CardContent>
            {loading ? (
              <div className="text-center py-8 text-muted-foreground">Loading...</div>
            ) : rewards.length === 0 ? (
              <div className="text-center py-8 text-muted-foreground">
                No rewards created yet. Create your first reward!
              </div>
            ) : (
              <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                {rewards.map((reward) => (
                  <Card key={reward.id}>
                    <CardHeader>
                      <div className="flex items-start justify-between">
                        <div className="flex-1">
                          <CardTitle className="text-lg">{reward.name}</CardTitle>
                          <div className="flex gap-2 mt-2">
                            <Badge variant={reward.isActive ? 'default' : 'secondary'}>
                              {reward.isActive ? 'Active' : 'Inactive'}
                            </Badge>
                            <Badge variant="outline">{reward.pointsRequired} pts</Badge>
                          </div>
                        </div>
                        <div className="flex gap-2">
                          <Button variant="ghost" size="sm" onClick={() => handleEdit(reward)}>
                            <Edit className="w-4 h-4" />
                          </Button>
                          <Button variant="ghost" size="sm" onClick={() => handleDelete(reward.id)}>
                            <Trash2 className="w-4 h-4" />
                          </Button>
                        </div>
                      </div>
                    </CardHeader>
                    <CardContent>
                      <p className="text-sm text-muted-foreground mb-3">{reward.description}</p>
                      {(reward.discountAmount || reward.discountPercentage) && (
                        <div className="text-sm mb-2">
                          <strong>Discount: </strong>
                          {reward.discountAmount && `$${reward.discountAmount}`}
                          {reward.discountPercentage && `${reward.discountPercentage}%`}
                        </div>
                      )}
                      <div className="text-sm text-muted-foreground">
                        Redeemed: {reward.currentRedemptions}
                        {reward.maxRedemptions && `/${reward.maxRedemptions}`}
                      </div>
                    </CardContent>
                  </Card>
                ))}
              </div>
            )}
          </CardContent>
        </Card>
      )}

      {/* Create/Edit Dialog */}
      <Dialog open={dialogOpen} onOpenChange={(open) => {
        setDialogOpen(open)
        if (!open) resetForm()
      }}>
        <DialogContent className="max-w-2xl max-h-[90vh] overflow-y-auto">
          <DialogHeader>
            <DialogTitle>
              {editingReward ? 'Edit Reward' : 'Create New Reward'}
            </DialogTitle>
            <DialogDescription>
              Configure the reward details and requirements
            </DialogDescription>
          </DialogHeader>
          <div className="space-y-4 py-4">
            <div>
              <Label>Reward Name *</Label>
              <Input
                value={formData.name}
                onChange={(e) => setFormData({ ...formData, name: e.target.value })}
                placeholder="e.g., Free Appetizer, 20% Off"
              />
            </div>

            <div>
              <Label>Description *</Label>
              <Textarea
                value={formData.description}
                onChange={(e) => setFormData({ ...formData, description: e.target.value })}
                placeholder="Describe what the customer gets"
                rows={3}
              />
            </div>

            <div>
              <Label>Points Required *</Label>
              <Input
                type="number"
                value={formData.pointsRequired}
                onChange={(e) => setFormData({ ...formData, pointsRequired: parseInt(e.target.value) || 0 })}
              />
            </div>

            <div className="grid grid-cols-2 gap-4">
              <div>
                <Label>Discount Amount ($)</Label>
                <Input
                  type="number"
                  step="0.01"
                  value={formData.discountAmount}
                  onChange={(e) => setFormData({ ...formData, discountAmount: e.target.value })}
                  placeholder="10.00"
                />
              </div>
              <div>
                <Label>Discount Percentage (%)</Label>
                <Input
                  type="number"
                  value={formData.discountPercentage}
                  onChange={(e) => setFormData({ ...formData, discountPercentage: e.target.value })}
                  placeholder="20"
                />
              </div>
            </div>

            <div>
              <Label>Image URL (Optional)</Label>
              <Input
                value={formData.imageUrl}
                onChange={(e) => setFormData({ ...formData, imageUrl: e.target.value })}
                placeholder="https://..."
              />
            </div>

            <div className="grid grid-cols-2 gap-4">
              <div>
                <Label>Start Date (Optional)</Label>
                <Input
                  type="datetime-local"
                  value={formData.startDate}
                  onChange={(e) => setFormData({ ...formData, startDate: e.target.value })}
                />
              </div>
              <div>
                <Label>End Date (Optional)</Label>
                <Input
                  type="datetime-local"
                  value={formData.endDate}
                  onChange={(e) => setFormData({ ...formData, endDate: e.target.value })}
                />
              </div>
            </div>

            <div>
              <Label>Max Redemptions (Optional)</Label>
              <Input
                type="number"
                value={formData.maxRedemptions}
                onChange={(e) => setFormData({ ...formData, maxRedemptions: e.target.value })}
                placeholder="Leave empty for unlimited"
              />
            </div>
          </div>
          <DialogFooter>
            <Button variant="outline" onClick={() => {
              setDialogOpen(false)
              resetForm()
            }}>
              Cancel
            </Button>
            <Button onClick={handleSubmit} disabled={loading}>
              {loading ? 'Saving...' : (editingReward ? 'Update' : 'Create')}
            </Button>
          </DialogFooter>
        </DialogContent>
      </Dialog>
    </div>
  )
}

'use client'

import { useEffect, useState } from 'react'
import { useParams, useRouter } from 'next/navigation'
import { Button } from '@/components/ui/button'
import { Card, CardContent, CardHeader, CardTitle, CardDescription } from '@/components/ui/card'
import { Badge } from '@/components/ui/badge'
import { useToast } from '@/hooks/use-toast'
import { loyaltyApi } from '@/lib/api'
import { ArrowLeft, Gift, Star, Clock, Users } from 'lucide-react'
import {
  Dialog,
  DialogContent,
  DialogDescription,
  DialogFooter,
  DialogHeader,
  DialogTitle,
} from '@/components/ui/dialog'

interface Reward {
  id: string
  restaurantId: string
  restaurantName: string
  name: string
  description: string
  pointsRequired: number
  discountAmount?: number
  discountPercentage?: number
  imageUrl?: string
  isActive: boolean
  startDate?: string
  endDate?: string
  maxRedemptions?: number
  currentRedemptions: number
  canRedeem: boolean
}

export default function RestaurantRewardsPage() {
  const params = useParams()
  const router = useRouter()
  const { toast } = useToast()
  const [rewards, setRewards] = useState<Reward[]>([])
  const [loading, setLoading] = useState(false)
  const [selectedReward, setSelectedReward] = useState<Reward | null>(null)
  const [confirmDialogOpen, setConfirmDialogOpen] = useState(false)
  const [userPoints, setUserPoints] = useState(0)

  const restaurantId = params.id as string

  useEffect(() => {
    fetchRewards()
    fetchUserPoints()
  }, [restaurantId])

  const fetchRewards = async () => {
    setLoading(true)
    try {
      const data = await loyaltyApi.getRestaurantRewards(restaurantId)
      setRewards(data)
    } catch (error: any) {
      toast({
        title: 'Error',
        description: error.message,
        variant: 'destructive',
      })
    } finally {
      setLoading(false)
    }
  }

  const fetchUserPoints = async () => {
    try {
      const balances = await loyaltyApi.customer.getBalance()
      const balance = balances.find((b: any) => b.restaurantId === restaurantId)
      setUserPoints(balance?.availablePoints || 0)
    } catch (error: any) {
      console.error(error)
    }
  }

  const handleRedeemClick = (reward: Reward) => {
    setSelectedReward(reward)
    setConfirmDialogOpen(true)
  }

  const handleConfirmRedeem = async () => {
    if (!selectedReward) return

    setLoading(true)
    try {
      const redemption = await loyaltyApi.customer.redeemReward(selectedReward.id)
      toast({
        title: 'Success!',
        description: `Reward redeemed! Your coupon code: ${redemption.couponCode}`,
      })
      setConfirmDialogOpen(false)
      fetchRewards()
      fetchUserPoints()
    } catch (error: any) {
      toast({
        title: 'Error',
        description: error.message,
        variant: 'destructive',
      })
    } finally {
      setLoading(false)
    }
  }

  return (
    <div className="container mx-auto p-6 max-w-6xl">
      <Button variant="ghost" onClick={() => router.back()} className="mb-4">
        <ArrowLeft className="w-4 h-4 mr-2" />
        Back
      </Button>

      <div className="mb-6">
        <h1 className="text-3xl font-bold">Loyalty Rewards</h1>
        <p className="text-muted-foreground">Redeem your points for exclusive rewards</p>
        <div className="mt-2">
          <Badge variant="outline" className="text-lg px-4 py-2">
            <Star className="w-4 h-4 mr-2" />
            Your Points: {userPoints}
          </Badge>
        </div>
      </div>

      {loading && rewards.length === 0 ? (
        <div className="text-center py-12 text-muted-foreground">Loading rewards...</div>
      ) : rewards.length === 0 ? (
        <Card>
          <CardContent className="py-12 text-center text-muted-foreground">
            <Gift className="w-16 h-16 mx-auto mb-4 opacity-20" />
            <p>No rewards available at this restaurant yet.</p>
          </CardContent>
        </Card>
      ) : (
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
          {rewards.map((reward) => {
            const isAvailable = reward.isActive && 
                               (!reward.maxRedemptions || reward.currentRedemptions < reward.maxRedemptions) &&
                               (!reward.endDate || new Date(reward.endDate) > new Date())
            const hasEnoughPoints = userPoints >= reward.pointsRequired

            return (
              <Card key={reward.id} className={!isAvailable ? 'opacity-60' : ''}>
                {reward.imageUrl && (
                  <div className="aspect-video w-full overflow-hidden rounded-t-lg">
                    <img 
                      src={reward.imageUrl} 
                      alt={reward.name}
                      className="w-full h-full object-cover"
                    />
                  </div>
                )}
                <CardHeader>
                  <div className="flex items-start justify-between">
                    <CardTitle className="text-lg">{reward.name}</CardTitle>
                    {!isAvailable && (
                      <Badge variant="secondary">Unavailable</Badge>
                    )}
                  </div>
                  <CardDescription className="mt-2">{reward.description}</CardDescription>
                </CardHeader>
                <CardContent>
                  <div className="space-y-3">
                    {/* Discount Info */}
                    {(reward.discountAmount || reward.discountPercentage) && (
                      <div className="bg-primary/10 p-3 rounded-lg text-center">
                        <div className="text-2xl font-bold text-primary">
                          {reward.discountAmount && `$${reward.discountAmount} OFF`}
                          {reward.discountPercentage && `${reward.discountPercentage}% OFF`}
                        </div>
                      </div>
                    )}

                    {/* Points Required */}
                    <div className="flex items-center justify-between">
                      <span className="text-sm text-muted-foreground">Points Required</span>
                      <Badge variant={hasEnoughPoints ? 'default' : 'secondary'} className="text-base px-3 py-1">
                        <Star className="w-4 h-4 mr-1" />
                        {reward.pointsRequired}
                      </Badge>
                    </div>

                    {/* Additional Info */}
                    <div className="space-y-1 text-xs text-muted-foreground">
                      {reward.maxRedemptions && (
                        <div className="flex items-center gap-2">
                          <Users className="w-3 h-3" />
                          <span>
                            {reward.currentRedemptions}/{reward.maxRedemptions} redeemed
                          </span>
                        </div>
                      )}
                      {reward.endDate && (
                        <div className="flex items-center gap-2">
                          <Clock className="w-3 h-3" />
                          <span>
                            Valid until {new Date(reward.endDate).toLocaleDateString()}
                          </span>
                        </div>
                      )}
                    </div>

                    {/* Redeem Button */}
                    <Button
                      onClick={() => handleRedeemClick(reward)}
                      disabled={!isAvailable || !hasEnoughPoints}
                      className="w-full"
                    >
                      {!hasEnoughPoints 
                        ? `Need ${reward.pointsRequired - userPoints} more points`
                        : 'Redeem Now'
                      }
                    </Button>
                  </div>
                </CardContent>
              </Card>
            )
          })}
        </div>
      )}

      {/* Confirm Redemption Dialog */}
      <Dialog open={confirmDialogOpen} onOpenChange={setConfirmDialogOpen}>
        <DialogContent>
          <DialogHeader>
            <DialogTitle>Confirm Redemption</DialogTitle>
            <DialogDescription>
              Are you sure you want to redeem this reward?
            </DialogDescription>
          </DialogHeader>
          {selectedReward && (
            <div className="py-4">
              <div className="space-y-3">
                <div>
                  <h4 className="font-semibold">{selectedReward.name}</h4>
                  <p className="text-sm text-muted-foreground">{selectedReward.description}</p>
                </div>
                <div className="bg-muted p-3 rounded-lg">
                  <div className="flex items-center justify-between mb-2">
                    <span className="text-sm">Points to Spend</span>
                    <span className="font-bold">-{selectedReward.pointsRequired}</span>
                  </div>
                  <div className="flex items-center justify-between">
                    <span className="text-sm">Remaining Balance</span>
                    <span className="font-bold">{userPoints - selectedReward.pointsRequired}</span>
                  </div>
                </div>
                <p className="text-xs text-muted-foreground">
                  You'll receive a coupon code that can be used at this restaurant.
                </p>
              </div>
            </div>
          )}
          <DialogFooter>
            <Button
              variant="outline"
              onClick={() => {
                setConfirmDialogOpen(false)
                setSelectedReward(null)
              }}
            >
              Cancel
            </Button>
            <Button onClick={handleConfirmRedeem} disabled={loading}>
              {loading ? 'Redeeming...' : 'Confirm Redeem'}
            </Button>
          </DialogFooter>
        </DialogContent>
      </Dialog>
    </div>
  )
}

