    public DateTime? UsedAt { get; set; }

    public string? RestaurantId { get; set; }
    public Restaurant? Restaurant { get; set; }
}

using Microsoft.EntityFrameworkCore;
using Microsoft.EntityFrameworkCore.Metadata.Builders;
using RestaurantManagment.Domain.Models;

namespace RestaurantManagment.Persistance.Configurations;

public class JobPostingConfiguration : IEntityTypeConfiguration<JobPosting>
{
    public void Configure(EntityTypeBuilder<JobPosting> builder)
    {
        builder.HasKey(j => j.Id);

        builder.Property(j => j.Title)
            .IsRequired()
            .HasMaxLength(200);

        builder.Property(j => j.Description)
            .IsRequired()
            .HasMaxLength(2000);

        builder.Property(j => j.Requirements)
            .IsRequired()
            .HasMaxLength(1000);

        builder.Property(j => j.Position)
            .IsRequired()
            .HasMaxLength(100);

        builder.Property(j => j.EmploymentType)
            .IsRequired()
            .HasMaxLength(50);

        // Relationships
        builder.HasOne(j => j.Restaurant)
            .WithMany(r => r.JobPostings)
            .HasForeignKey(j => j.RestaurantId)
            .OnDelete(DeleteBehavior.Cascade);

        // Indexes
        builder.HasIndex(j => j.RestaurantId);
        builder.HasIndex(j => j.IsActive);

        // Soft Delete Query Filter
        builder.HasQueryFilter(j => !j.IsDeleted);
    }
}

using Microsoft.AspNetCore.Authorization;
using Microsoft.AspNetCore.Mvc;
using RestaurantManagment.Application.Common.Interfaces;

namespace RestaurantManagment.WebAPI.Controllers;

[ApiController]
[Route("api/[controller]")]
public class FileController : ControllerBase
{
    private readonly IFileService _fileService;

    public FileController(IFileService fileService)
    {
        _fileService = fileService;
    }

    /// <summary>
    /// Upload profile image for user
    /// </summary>
    [HttpPost("upload/profile/{userId}")]
    [Authorize]
    public async Task<IActionResult> UploadProfileImage(string userId, IFormFile file)
    {
        try
        {
            if (file == null || file.Length == 0)
                return BadRequest(new { message = "Dosya yüklenemedi." });

            var fileUrl = await _fileService.UploadProfileImageAsync(file, userId);
            return Ok(new { imageUrl = fileUrl, message = "Profil resmi başarıyla yüklendi." });
        }
        catch (ArgumentException ex)
        {
            return BadRequest(new { message = ex.Message });
        }
        catch (Exception ex)
        {
            return StatusCode(500, new { message = "Dosya yüklenirken bir hata oluştu.", error = ex.Message });
        }
    }

    /// <summary>
    /// Upload menu item image
    /// </summary>
    [HttpPost("upload/menuitem/{menuItemId}")]
    [Authorize(Roles = "Owner,Admin")]
    public async Task<IActionResult> UploadMenuItemImage(string menuItemId, IFormFile file)
    {
        try
        {
            if (file == null || file.Length == 0)
                return BadRequest(new { message = "Dosya yüklenemedi." });

            var fileUrl = await _fileService.UploadMenuItemImageAsync(file, menuItemId);
            return Ok(new { imageUrl = fileUrl, message = "Menü öğesi resmi başarıyla yüklendi." });
        }
        catch (ArgumentException ex)
        {
            return BadRequest(new { message = ex.Message });
        }
        catch (Exception ex)
        {
            return StatusCode(500, new { message = "Dosya yüklenirken bir hata oluştu.", error = ex.Message });
        }
    }

    /// <summary>
    /// Upload reward image
    /// </summary>
    [HttpPost("upload/reward/{rewardId}")]
    [Authorize(Roles = "Owner,Admin")]
    public async Task<IActionResult> UploadRewardImage(string rewardId, IFormFile file)
    {
        try
        {
            if (file == null || file.Length == 0)
                return BadRequest(new { message = "Dosya yüklenemedi." });

            var fileUrl = await _fileService.UploadRewardImageAsync(file, rewardId);
            return Ok(new { imageUrl = fileUrl, message = "Ödül resmi başarıyla yüklendi." });
        }
        catch (ArgumentException ex)
        {
            return BadRequest(new { message = ex.Message });
        }
        catch (Exception ex)
        {
            return StatusCode(500, new { message = "Dosya yüklenirken bir hata oluştu.", error = ex.Message });
        }
    }

    /// <summary>
    /// Upload restaurant image
    /// </summary>
    [HttpPost("upload/restaurant/{restaurantId}")]
    [Authorize(Roles = "Owner,Admin")]
    public async Task<IActionResult> UploadRestaurantImage(string restaurantId, IFormFile file)
    {
        try
        {
            if (file == null || file.Length == 0)
                return BadRequest(new { message = "Dosya yüklenemedi." });

            var fileUrl = await _fileService.UploadRestaurantImageAsync(file, restaurantId);
            return Ok(new { imageUrl = fileUrl, message = "Restoran resmi başarıyla yüklendi." });
        }
        catch (ArgumentException ex)
        {
            return BadRequest(new { message = ex.Message });
        }
        catch (Exception ex)
        {
            return StatusCode(500, new { message = "Dosya yüklenirken bir hata oluştu.", error = ex.Message });
        }
    }

    /// <summary>
    /// Delete a file
    /// </summary>
    [HttpDelete("delete")]
    [Authorize]
    public async Task<IActionResult> DeleteFile([FromQuery] string fileUrl)
    {
        try
        {
            var result = await _fileService.DeleteFileAsync(fileUrl);
            if (result)
                return Ok(new { message = "Dosya başarıyla silindi." });

            return NotFound(new { message = "Dosya bulunamadı." });
        }
        catch (Exception ex)
        {
            return StatusCode(500, new { message = "Dosya silinirken bir hata oluştu.", error = ex.Message });
        }
    }

    /// <summary>
    /// Check if file exists
    /// </summary>
    [HttpGet("exists")]
    public async Task<IActionResult> FileExists([FromQuery] string fileUrl)
    {
        try
        {
            var exists = await _fileService.FileExistsAsync(fileUrl);
            return Ok(new { exists });
        }
        catch (Exception ex)
        {
            return StatusCode(500, new { message = "Dosya kontrol edilirken bir hata oluştu.", error = ex.Message });
        }
    }
}

using Microsoft.AspNetCore.Authorization;
using Microsoft.AspNetCore.Mvc;
using RestaurantManagment.Application.Common.DTOs.Loyalty;
using RestaurantManagment.Application.Common.Interfaces;
using System.Security.Claims;

namespace RestaurantManagment.WebAPI.Controllers;

[Route("api/[controller]")]
[ApiController]
public class LoyaltyController : ControllerBase
{
    private readonly ILoyaltyService _loyaltyService;

    public LoyaltyController(ILoyaltyService loyaltyService)
    {
        _loyaltyService = loyaltyService;
    }

    // ADMIN ENDPOINTS - Generate Loyalty Codes
    [HttpPost("admin/codes")]
    [Authorize(Roles = "Admin")]
    public async Task<IActionResult> GenerateLoyaltyCode([FromBody] CreateLoyaltyCodeDto dto)
    {
        try
        {
            var adminId = User.FindFirst(ClaimTypes.NameIdentifier)?.Value;
            if (string.IsNullOrEmpty(adminId))
                return Unauthorized();

            var code = await _loyaltyService.GenerateLoyaltyCodeAsync(dto, adminId);
            return Ok(code);
        }
        catch (Exception ex)
        {
            return BadRequest(new { message = ex.Message });
        }
    }

    [HttpGet("admin/codes")]
    [Authorize(Roles = "Admin")]
    public async Task<IActionResult> GetAllLoyaltyCodes()
    {
        try
        {
            var codes = await _loyaltyService.GetAllLoyaltyCodesAsync();
            return Ok(codes);
        }
        catch (Exception ex)
        {
            return StatusCode(500, new { message = ex.Message });
        }
    }

    [HttpGet("admin/codes/{codeId}")]
    [Authorize(Roles = "Admin")]
    public async Task<IActionResult> GetLoyaltyCodeById(string codeId)
    {
        try
        {
            var code = await _loyaltyService.GetLoyaltyCodeByIdAsync(codeId);
            if (code == null)
                return NotFound(new { message = "Code not found" });

            return Ok(code);
        }
        catch (Exception ex)
        {
            return StatusCode(500, new { message = ex.Message });
        }
    }

    [HttpPatch("admin/codes/{codeId}/deactivate")]
    [Authorize(Roles = "Admin")]
    public async Task<IActionResult> DeactivateLoyaltyCode(string codeId)
    {
        try
        {
            var result = await _loyaltyService.DeactivateLoyaltyCodeAsync(codeId);
            if (!result)
                return NotFound(new { message = "Code not found" });

            return Ok(new { message = "Code deactivated successfully" });
        }
        catch (Exception ex)
        {
            return BadRequest(new { message = ex.Message });
        }
    }

    // CUSTOMER ENDPOINTS - Redeem Codes and Points
    [HttpPost("customer/redeem-code")]
    [Authorize(Roles = "Customer")]
    public async Task<IActionResult> RedeemLoyaltyCode([FromBody] RedeemLoyaltyCodeDto dto)
    {
        try
        {
            var customerId = User.FindFirst(ClaimTypes.NameIdentifier)?.Value;
            if (string.IsNullOrEmpty(customerId))
                return Unauthorized();

            var point = await _loyaltyService.RedeemLoyaltyCodeAsync(dto.Code, customerId);
            return Ok(point);
        }
        catch (Exception ex)
        {
            return BadRequest(new { message = ex.Message });
        }
    }

    [HttpGet("customer/balance")]
    [Authorize(Roles = "Customer")]
    public async Task<IActionResult> GetCustomerLoyaltyBalance()
    {
        try
        {
            var customerId = User.FindFirst(ClaimTypes.NameIdentifier)?.Value;
            if (string.IsNullOrEmpty(customerId))
                return Unauthorized();

            var balance = await _loyaltyService.GetCustomerLoyaltyBalanceAsync(customerId);
            return Ok(balance);
        }
        catch (Exception ex)
        {
            return StatusCode(500, new { message = ex.Message });
        }
    }

    [HttpGet("customer/history")]
    [Authorize(Roles = "Customer")]
    public async Task<IActionResult> GetCustomerPointHistory([FromQuery] string? restaurantId = null)
    {
        try
        {
            var customerId = User.FindFirst(ClaimTypes.NameIdentifier)?.Value;
            if (string.IsNullOrEmpty(customerId))
                return Unauthorized();

            var history = await _loyaltyService.GetCustomerPointHistoryAsync(customerId, restaurantId);
            return Ok(history);
        }
        catch (Exception ex)
        {
            return StatusCode(500, new { message = ex.Message });
        }
    }

    // OWNER ENDPOINTS - Reward Management
    [HttpPost("owner/rewards")]
    [Authorize(Roles = "RestaurantOwner")]
    public async Task<IActionResult> CreateReward([FromBody] CreateRewardDto dto)
    {
        try
        {
            var ownerId = User.FindFirst(ClaimTypes.NameIdentifier)?.Value;
            if (string.IsNullOrEmpty(ownerId))
                return Unauthorized();

            var reward = await _loyaltyService.CreateRewardAsync(dto, ownerId);
            return Ok(reward);
        }
        catch (Exception ex)
        {
            return BadRequest(new { message = ex.Message });
        }
    }

    [HttpPut("owner/rewards/{rewardId}")]
    [Authorize(Roles = "RestaurantOwner")]
    public async Task<IActionResult> UpdateReward(string rewardId, [FromBody] UpdateRewardDto dto)
    {
        try
        {
            var ownerId = User.FindFirst(ClaimTypes.NameIdentifier)?.Value;
            if (string.IsNullOrEmpty(ownerId))
                return Unauthorized();

            var reward = await _loyaltyService.UpdateRewardAsync(rewardId, dto, ownerId);
            return Ok(reward);
        }
        catch (Exception ex)
        {
            return BadRequest(new { message = ex.Message });
        }
    }

    [HttpDelete("owner/rewards/{rewardId}")]
    [Authorize(Roles = "RestaurantOwner")]
    public async Task<IActionResult> DeleteReward(string rewardId)
    {
        try
        {
            var ownerId = User.FindFirst(ClaimTypes.NameIdentifier)?.Value;
            if (string.IsNullOrEmpty(ownerId))
                return Unauthorized();

            var result = await _loyaltyService.DeleteRewardAsync(rewardId, ownerId);
            if (!result)
                return NotFound(new { message = "Reward not found" });

            return Ok(new { message = "Reward deleted successfully" });
        }
        catch (Exception ex)
        {
            return BadRequest(new { message = ex.Message });
        }
    }

    // PUBLIC/CUSTOMER ENDPOINTS - View Rewards
    [HttpGet("restaurants/{restaurantId}/rewards")]
    public async Task<IActionResult> GetRestaurantRewards(string restaurantId)
    {
        try
        {
            var customerId = User.FindFirst(ClaimTypes.NameIdentifier)?.Value;
            var rewards = await _loyaltyService.GetRestaurantRewardsAsync(restaurantId, customerId);
            return Ok(rewards);
        }
        catch (Exception ex)
        {
            return StatusCode(500, new { message = ex.Message });
        }
    }

    [HttpGet("rewards/{rewardId}")]
    public async Task<IActionResult> GetRewardById(string rewardId)
    {
        try
        {
            var customerId = User.FindFirst(ClaimTypes.NameIdentifier)?.Value;
            var reward = await _loyaltyService.GetRewardByIdAsync(rewardId, customerId);
            if (reward == null)
                return NotFound(new { message = "Reward not found" });

            return Ok(reward);
        }
        catch (Exception ex)
        {
            return StatusCode(500, new { message = ex.Message });
        }
    }

    // CUSTOMER ENDPOINTS - Redeem Rewards
    [HttpPost("customer/redeem-reward")]
    [Authorize(Roles = "Customer")]
    public async Task<IActionResult> RedeemReward([FromBody] RedeemRewardDto dto)
    {
        try
        {
            var customerId = User.FindFirst(ClaimTypes.NameIdentifier)?.Value;
            if (string.IsNullOrEmpty(customerId))
                return Unauthorized();

            var redemption = await _loyaltyService.RedeemRewardAsync(dto.RewardId, customerId);
            return Ok(redemption);
        }
        catch (Exception ex)
        {
            return BadRequest(new { message = ex.Message });
        }
    }

    [HttpGet("customer/redemptions")]
    [Authorize(Roles = "Customer")]
    public async Task<IActionResult> GetCustomerRedemptions()
    {
        try
        {
            var customerId = User.FindFirst(ClaimTypes.NameIdentifier)?.Value;
            if (string.IsNullOrEmpty(customerId))
                return Unauthorized();

            var redemptions = await _loyaltyService.GetCustomerRedemptionsAsync(customerId);
            return Ok(redemptions);
        }
        catch (Exception ex)
        {
            return StatusCode(500, new { message = ex.Message });
        }
    }

    [HttpGet("customer/redemptions/{redemptionId}")]
    [Authorize(Roles = "Customer")]
    public async Task<IActionResult> GetRedemptionById(string redemptionId)
    {
        try
        {
            var customerId = User.FindFirst(ClaimTypes.NameIdentifier)?.Value;
            if (string.IsNullOrEmpty(customerId))
                return Unauthorized();

            var redemption = await _loyaltyService.GetRedemptionByIdAsync(redemptionId, customerId);
            if (redemption == null)
                return NotFound(new { message = "Redemption not found" });

            return Ok(redemption);
        }
        catch (Exception ex)
        {
            return StatusCode(500, new { message = ex.Message });
        }
    }
}

using Microsoft.AspNetCore.Authorization;
using Microsoft.AspNetCore.Mvc;
using RestaurantManagment.Application.Common.Interfaces;

namespace RestaurantManagment.WebAPI.Controllers;

[ApiController]
[Route("api/[controller]")]
[Authorize(Roles = "Owner,Admin")]
public class MenuItemImageController : ControllerBase
{
    private readonly IFileService _fileService;

    public MenuItemImageController(IFileService fileService)
    {
        _fileService = fileService;
    }

    /// <summary>
    /// Upload menu item image (Required when creating menu item)
    /// </summary>
    [HttpPost("{menuItemId}")]
    public async Task<IActionResult> UploadMenuItemImage(string menuItemId, [FromForm] IFormFile file)
    {
        try
        {
            if (file == null || file.Length == 0)
                return BadRequest(new { message = "Menü öğesi resmi zorunludur." });

            var fileUrl = await _fileService.UploadMenuItemImageAsync(file, menuItemId);
            return Ok(new { imageUrl = fileUrl, message = "Menü öğesi resmi başarıyla yüklendi." });
        }
        catch (ArgumentException ex)
        {
            return BadRequest(new { message = ex.Message });
        }
        catch (Exception ex)
        {
            return StatusCode(500, new { message = "Menü öğesi resmi yüklenirken bir hata oluştu.", error = ex.Message });
        }
    }

    /// <summary>
    /// Update menu item image
    /// </summary>
    [HttpPut("{menuItemId}")]
    public async Task<IActionResult> UpdateMenuItemImage(string menuItemId, [FromForm] IFormFile file)
    {
        try
        {
            if (file == null || file.Length == 0)
                return BadRequest(new { message = "Menü öğesi resmi zorunludur." });

            var fileUrl = await _fileService.UploadMenuItemImageAsync(file, menuItemId);
            return Ok(new { imageUrl = fileUrl, message = "Menü öğesi resmi başarıyla güncellendi." });
        }
        catch (ArgumentException ex)
        {
            return BadRequest(new { message = ex.Message });
        }
        catch (Exception ex)
        {
            return StatusCode(500, new { message = "Menü öğesi resmi güncellenirken bir hata oluştu.", error = ex.Message });
        }
    }

    /// <summary>
    /// Delete menu item image
    /// </summary>
    [HttpDelete]
    public async Task<IActionResult> DeleteMenuItemImage([FromQuery] string fileUrl)
    {
        try
        {
            var result = await _fileService.DeleteFileAsync(fileUrl);
            if (result)
                return Ok(new { message = "Menü öğesi resmi başarıyla silindi." });

            return NotFound(new { message = "Menü öğesi resmi bulunamadı." });
        }
        catch (Exception ex)
        {
            return StatusCode(500, new { message = "Menü öğesi resmi silinirken bir hata oluştu.", error = ex.Message });
        }
    }
}

