'use client'

import { useEffect, useState } from 'react'
import { useParams, useRouter } from 'next/navigation'
import { Button } from '@/components/ui/button'
import { Card, CardContent, CardHeader, CardTitle, CardDescription } from '@/components/ui/card'
import { Badge } from '@/components/ui/badge'
import { Label } from '@/components/ui/label'
import { Textarea } from '@/components/ui/textarea'
import { ArrowLeft, CheckCircle, XCircle, User, Building, MapPin, Phone, Mail, Calendar } from 'lucide-react'
import { api } from '@/lib/api'
import { useToast } from '@/hooks/use-toast'
import {
  Dialog,
  DialogContent,
  DialogDescription,
  DialogFooter,
  DialogHeader,
  DialogTitle,
} from '@/components/ui/dialog'

interface Application {
  id: string
  userId: string
  businessName: string
  businessDescription: string
  businessAddress: string
  businessPhone: string
  businessEmail: string
  category: string
  additionalNotes?: string
  status: string
  applicationDate: string
  reviewedBy?: string
  reviewedAt?: string
  rejectionReason?: string
  user: {
    firstName: string
    lastName: string
  }
}

export default function ApplicationDetailPage() {
  const params = useParams()
  const router = useRouter()
  const { toast } = useToast()
  const [application, setApplication] = useState<Application | null>(null)
  const [loading, setLoading] = useState(true)
  const [actionLoading, setActionLoading] = useState(false)
  const [rejectDialogOpen, setRejectDialogOpen] = useState(false)
  const [rejectionReason, setRejectionReason] = useState('')

  useEffect(() => {
    fetchApplication()
  }, [params.id])

  const fetchApplication = async () => {
    setLoading(true)
    try {
      const data = await api.get<Application>(`/admin/applications/${params.id}`)
      setApplication(data)
    } catch (error: any) {
      console.error('Error fetching application:', error)
      toast({
        title: 'Error',
        description: error.message || 'Failed to load application',
        variant: 'destructive',
      })
    } finally {
      setLoading(false)
    }
  }

  const handleApprove = async () => {
    if (!confirm('Are you sure you want to approve this application? This will create a restaurant and grant the user Restaurant Owner role.')) return

    setActionLoading(true)
    try {
      await api.post(`/admin/applications/${params.id}/approve`, {})
      toast({
        title: 'Success',
        description: 'Application approved successfully',
      })
      router.push('/admin/applications')
    } catch (error: any) {
      console.error('Error approving application:', error)
      toast({
        title: 'Error',
        description: error.message || 'Failed to approve application',
        variant: 'destructive',
      })
    } finally {
      setActionLoading(false)
    }
  }

  const handleReject = async () => {
    if (!rejectionReason.trim()) {
      toast({
        title: 'Error',
        description: 'Please provide a rejection reason',
        variant: 'destructive',
      })
      return
    }

    setActionLoading(true)
    try {
      await api.post(`/admin/applications/${params.id}/reject`, { reason: rejectionReason })
      toast({
        title: 'Success',
        description: 'Application rejected successfully',
      })
      setRejectDialogOpen(false)
      router.push('/admin/applications')
    } catch (error: any) {
      console.error('Error rejecting application:', error)
      toast({
        title: 'Error',
        description: error.message || 'Failed to reject application',
        variant: 'destructive',
      })
    } finally {
      setActionLoading(false)
    }
  }

  const getStatusBadge = (status: string) => {
    switch (status.toLowerCase()) {
      case 'pending':
        return <Badge variant="outline" className="bg-yellow-100">Pending</Badge>
      case 'approved':
        return <Badge variant="default" className="bg-green-600">Approved</Badge>
      case 'rejected':
        return <Badge variant="destructive">Rejected</Badge>
      default:
        return <Badge variant="outline">{status}</Badge>
    }
  }

  if (loading) {
    return (
      <div className="container mx-auto p-6">
        <div className="text-center py-8">Loading application details...</div>
      </div>
    )
  }

  if (!application) {
    return (
      <div className="container mx-auto p-6">
        <Card>
          <CardHeader>
            <CardTitle>Application Not Found</CardTitle>
          </CardHeader>
        </Card>
      </div>
    )
  }

  return (
    <div className="container mx-auto p-6 max-w-4xl">
      <Button
        variant="ghost"
        onClick={() => router.push('/admin/applications')}
        className="mb-4"
      >
        <ArrowLeft className="w-4 h-4 mr-2" />
        Back to Applications
      </Button>

      <div className="space-y-6">
        {/* Header Card */}
        <Card>
          <CardHeader>
            <div className="flex items-center justify-between">
              <div>
                <CardTitle className="text-2xl">Restaurant Ownership Application</CardTitle>
                <CardDescription>Application ID: {application.id}</CardDescription>
              </div>
              {getStatusBadge(application.status)}
            </div>
          </CardHeader>
        </Card>

        {/* Applicant Information */}
        <Card>
          <CardHeader>
            <CardTitle className="flex items-center gap-2">
              <User className="w-5 h-5" />
              Applicant Information
            </CardTitle>
          </CardHeader>
          <CardContent className="space-y-3">
            <div className="grid grid-cols-2 gap-4">
              <div>
                <Label className="text-muted-foreground">Name</Label>
                <p className="font-medium">{application.user.firstName} {application.user.lastName}</p>
              </div>
              <div>
                <Label className="text-muted-foreground">Application Date</Label>
                <p className="font-medium flex items-center gap-1">
                  <Calendar className="w-4 h-4" />
                  {new Date(application.applicationDate).toLocaleDateString()}
                </p>
              </div>
            </div>
          </CardContent>
        </Card>

        {/* Business Information */}
        <Card>
          <CardHeader>
            <CardTitle className="flex items-center gap-2">
              <Building className="w-5 h-5" />
              Business Information
            </CardTitle>
          </CardHeader>
          <CardContent className="space-y-4">
            <div>
              <Label className="text-muted-foreground">Business Name</Label>
              <p className="font-medium text-lg">{application.businessName}</p>
            </div>
            <div>
              <Label className="text-muted-foreground">Category</Label>
              <div className="mt-1">
                <Badge variant="outline">{application.category}</Badge>
              </div>
            </div>
            <div>
              <Label className="text-muted-foreground">Description</Label>
              <p className="text-sm">{application.businessDescription}</p>
            </div>
            <div className="grid grid-cols-2 gap-4">
              <div>
                <Label className="text-muted-foreground flex items-center gap-1">
                  <MapPin className="w-4 h-4" />
                  Address
                </Label>
                <p className="text-sm">{application.businessAddress}</p>
              </div>
              <div>
                <Label className="text-muted-foreground flex items-center gap-1">
                  <Phone className="w-4 h-4" />
                  Phone
                </Label>
                <p className="text-sm">{application.businessPhone}</p>
              </div>
            </div>
            {application.businessEmail && (
              <div>
                <Label className="text-muted-foreground flex items-center gap-1">
                  <Mail className="w-4 h-4" />
                  Email
                </Label>
                <p className="text-sm">{application.businessEmail}</p>
              </div>
            )}
            {application.additionalNotes && (
              <div>
                <Label className="text-muted-foreground">Additional Notes</Label>
                <p className="text-sm">{application.additionalNotes}</p>
              </div>
            )}
          </CardContent>
        </Card>

        {/* Review Information */}
        {(application.reviewedAt || application.rejectionReason) && (
          <Card>
            <CardHeader>
              <CardTitle>Review Information</CardTitle>
            </CardHeader>
            <CardContent className="space-y-3">
              {application.reviewedAt && (
                <div>
                  <Label className="text-muted-foreground">Reviewed At</Label>
                  <p className="text-sm">{new Date(application.reviewedAt).toLocaleString()}</p>
                </div>
              )}
              {application.rejectionReason && (
                <div>
                  <Label className="text-muted-foreground">Rejection Reason</Label>
                  <p className="text-sm">{application.rejectionReason}</p>
                </div>
              )}
            </CardContent>
          </Card>
        )}

        {/* Actions */}
        {application.status.toLowerCase() === 'pending' && (
          <Card>
            <CardHeader>
              <CardTitle>Actions</CardTitle>
              <CardDescription>Review and make a decision on this application</CardDescription>
            </CardHeader>
            <CardContent>
              <div className="flex gap-4">
                <Button
                  onClick={handleApprove}
                  disabled={actionLoading}
                  className="flex-1"
                >
                  <CheckCircle className="w-4 h-4 mr-2" />
                  Approve Application
                </Button>
                <Button
                  variant="destructive"
                  onClick={() => setRejectDialogOpen(true)}
                  disabled={actionLoading}
                  className="flex-1"
                >
                  <XCircle className="w-4 h-4 mr-2" />
                  Reject Application
                </Button>
              </div>
            </CardContent>
          </Card>
        )}
      </div>

      {/* Reject Dialog */}
      <Dialog open={rejectDialogOpen} onOpenChange={setRejectDialogOpen}>
        <DialogContent>
          <DialogHeader>
            <DialogTitle>Reject Application</DialogTitle>
            <DialogDescription>
              Please provide a reason for rejecting this application. This will be visible to the applicant.
            </DialogDescription>
          </DialogHeader>
          <div className="py-4">
            <Textarea
              placeholder="Enter rejection reason..."
              value={rejectionReason}
              onChange={(e) => setRejectionReason(e.target.value)}
              rows={4}
            />
          </div>
          <DialogFooter>
            <Button
              variant="outline"
              onClick={() => setRejectDialogOpen(false)}
              disabled={actionLoading}
            >
              Cancel
            </Button>
            <Button
              variant="destructive"
              onClick={handleReject}
              disabled={actionLoading || !rejectionReason.trim()}
            >
              {actionLoading ? 'Rejecting...' : 'Reject Application'}
            </Button>
          </DialogFooter>
        </DialogContent>
      </Dialog>
    </div>
  )
}

'use client'

import { useEffect, useState } from 'react'
import { useParams, useRouter } from 'next/navigation'
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card'
import { Button } from '@/components/ui/button'
import { ArrowLeft, Users, ShoppingCart, DollarSign, Briefcase, Table, UtensilsCrossed, Star, TrendingUp } from 'lucide-react'

interface Statistics {
  totalCustomers: number
  totalOrders: number
  totalRevenue: number
  totalEmployees: number
  totalTables: number
  availableTables: number
  totalMenuItems: number
  averageOrderValue: number
  averageRating: number
  totalReviews: number
}

export default function StatisticsPage() {
  const params = useParams()
  const router = useRouter()
  const restaurantId = params.restaurantId as string
  const [statistics, setStatistics] = useState<Statistics | null>(null)
  const [loading, setLoading] = useState(true)

  useEffect(() => {
    fetchStatistics()
  }, [restaurantId])

  const fetchStatistics = async () => {
    try {
      const token = localStorage.getItem('token')
      const response = await fetch(
        `${process.env.NEXT_PUBLIC_API_URL}/api/Owner/restaurants/${restaurantId}/statistics`,
        {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        }
      )

      if (response.ok) {
        const data = await response.json()
        setStatistics(data)
      }
    } catch (error) {
      console.error('Failed to fetch statistics:', error)
    } finally {
      setLoading(false)
    }
  }

  if (loading) {
    return (
      <div className="container mx-auto p-6">
        <div className="flex items-center justify-center h-64">
          <p>Loading statistics...</p>
        </div>
      </div>
    )
  }

  if (!statistics) {
    return (
      <div className="container mx-auto p-6">
        <div className="flex items-center justify-center h-64">
          <p>Failed to load statistics</p>
        </div>
      </div>
    )
  }

  const stats = [
    {
      title: 'Total Customers',
      value: statistics.totalCustomers,
      icon: Users,
      color: 'text-blue-600',
      bgColor: 'bg-blue-100',
    },
    {
      title: 'Total Orders',
      value: statistics.totalOrders,
      icon: ShoppingCart,
      color: 'text-green-600',
      bgColor: 'bg-green-100',
    },
    {
      title: 'Total Revenue',
      value: `₺${statistics.totalRevenue.toLocaleString('tr-TR', { minimumFractionDigits: 2 })}`,
      icon: DollarSign,
      color: 'text-emerald-600',
      bgColor: 'bg-emerald-100',
    },
    {
      title: 'Average Order Value',
      value: `₺${statistics.averageOrderValue.toLocaleString('tr-TR', { minimumFractionDigits: 2 })}`,
      icon: TrendingUp,
      color: 'text-purple-600',
      bgColor: 'bg-purple-100',
    },
    {
      title: 'Total Employees',
      value: statistics.totalEmployees,
      icon: Briefcase,
      color: 'text-orange-600',
      bgColor: 'bg-orange-100',
    },
    {
      title: 'Total Tables',
      value: statistics.totalTables,
      icon: Table,
      color: 'text-indigo-600',
      bgColor: 'bg-indigo-100',
    },
    {
      title: 'Available Tables',
      value: statistics.availableTables,
      icon: Table,
      color: 'text-cyan-600',
      bgColor: 'bg-cyan-100',
    },
    {
      title: 'Total Menu Items',
      value: statistics.totalMenuItems,
      icon: UtensilsCrossed,
      color: 'text-pink-600',
      bgColor: 'bg-pink-100',
    },
    {
      title: 'Average Rating',
      value: statistics.averageRating.toFixed(2),
      icon: Star,
      color: 'text-yellow-600',
      bgColor: 'bg-yellow-100',
    },
    {
      title: 'Total Reviews',
      value: statistics.totalReviews,
      icon: Star,
      color: 'text-amber-600',
      bgColor: 'bg-amber-100',
    },
  ]

  return (
    <div className="container mx-auto p-6">
      <Button
        variant="ghost"
        className="mb-4"
        onClick={() => router.push(`/owner/restaurants/${restaurantId}/dashboard`)}
      >
        <ArrowLeft className="w-4 h-4 mr-2" />
        Back to Dashboard
      </Button>

      <div className="mb-6">
        <h1 className="text-3xl font-bold">Restaurant Statistics</h1>
        <p className="text-muted-foreground">Overview of your restaurant's performance</p>
      </div>

      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-4">
        {stats.map((stat, index) => {
          const Icon = stat.icon
          return (
            <Card key={index}>
              <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
                <CardTitle className="text-sm font-medium">{stat.title}</CardTitle>
                <div className={`p-2 rounded-full ${stat.bgColor}`}>
                  <Icon className={`h-4 w-4 ${stat.color}`} />
                </div>
              </CardHeader>
              <CardContent>
                <div className="text-2xl font-bold">{stat.value}</div>
              </CardContent>
            </Card>
          )
        })}
      </div>
    </div>
  )
}

