        }
    }

    /// <summary>
    /// Delete restaurant image
    /// </summary>
    [HttpDelete]
    public async Task<IActionResult> DeleteRestaurantImage([FromQuery] string fileUrl)
    {
        try
        {
            var result = await _fileService.DeleteFileAsync(fileUrl);
            if (result)
                return Ok(new { message = "Restoran resmi başarıyla silindi." });

            return NotFound(new { message = "Restoran resmi bulunamadı." });
        }
        catch (Exception ex)
        {
            return StatusCode(500, new { message = "Restoran resmi silinirken bir hata oluştu.", error = ex.Message });
        }
    }
}

using Microsoft.AspNetCore.Authorization;
using Microsoft.AspNetCore.Mvc;
using RestaurantManagment.Application.Common.Interfaces;

namespace RestaurantManagment.WebAPI.Controllers;

[ApiController]
[Route("api/[controller]")]
[Authorize(Roles = "Owner,Admin")]
public class RewardImageController : ControllerBase
{
    private readonly IFileService _fileService;

    public RewardImageController(IFileService fileService)
    {
        _fileService = fileService;
    }

    /// <summary>
    /// Upload reward image (Required when creating reward)
    /// </summary>
    [HttpPost("{rewardId}")]
    public async Task<IActionResult> UploadRewardImage(string rewardId, [FromForm] IFormFile file)
    {
        try
        {
            if (file == null || file.Length == 0)
                return BadRequest(new { message = "Ödül resmi zorunludur." });

            var fileUrl = await _fileService.UploadRewardImageAsync(file, rewardId);
            return Ok(new { imageUrl = fileUrl, message = "Ödül resmi başarıyla yüklendi." });
        }
        catch (ArgumentException ex)
        {
            return BadRequest(new { message = ex.Message });
        }
        catch (Exception ex)
        {
            return StatusCode(500, new { message = "Ödül resmi yüklenirken bir hata oluştu.", error = ex.Message });
        }
    }

    /// <summary>
    /// Update reward image
    /// </summary>
    [HttpPut("{rewardId}")]
    public async Task<IActionResult> UpdateRewardImage(string rewardId, [FromForm] IFormFile file)
    {
        try
        {
            if (file == null || file.Length == 0)
                return BadRequest(new { message = "Ödül resmi zorunludur." });

            var fileUrl = await _fileService.UploadRewardImageAsync(file, rewardId);
            return Ok(new { imageUrl = fileUrl, message = "Ödül resmi başarıyla güncellendi." });
        }
        catch (ArgumentException ex)
        {
            return BadRequest(new { message = ex.Message });
        }
        catch (Exception ex)
        {
            return StatusCode(500, new { message = "Ödül resmi güncellenirken bir hata oluştu.", error = ex.Message });
        }
    }

    /// <summary>
    /// Delete reward image
    /// </summary>
    [HttpDelete]
    public async Task<IActionResult> DeleteRewardImage([FromQuery] string fileUrl)
    {
        try
        {
            var result = await _fileService.DeleteFileAsync(fileUrl);
            if (result)
                return Ok(new { message = "Ödül resmi başarıyla silindi." });

            return NotFound(new { message = "Ödül resmi bulunamadı." });
        }
        catch (Exception ex)
        {
            return StatusCode(500, new { message = "Ödül resmi silinirken bir hata oluştu.", error = ex.Message });
        }
    }
}

using Microsoft.EntityFrameworkCore;
using Microsoft.AspNetCore.Identity;
using RestaurantManagment.Domain.Models;
using RestaurantManagment.Persistance.Data;

namespace RestaurantManagment.WebAPI;

public static class OwnerIdFixer
{
    public static async Task FixRestaurantOwnerIds(IServiceProvider services)
    {
        using var scope = services.CreateScope();
        var context = scope.ServiceProvider.GetRequiredService<AppDbContext>();
        var userManager = scope.ServiceProvider.GetRequiredService<UserManager<AppUser>>();
        var logger = scope.ServiceProvider.GetRequiredService<ILogger<Program>>();

        try
        {
            // Owner kullanıcısını bul
            var owner = await userManager.FindByEmailAsync("owner@gmail.com");
            if (owner == null)
            {
                logger.LogWarning("owner@gmail.com kullanıcısı bulunamadı!");
                return;
            }

            logger.LogInformation($"Owner bulundu: {owner.Email} (ID: {owner.Id})");

            // Tüm aktif restoranları bul
            var restaurants = await context.Restaurants
                .Where(r => !r.IsDeleted)
                .ToListAsync();

            if (restaurants.Count == 0)
            {
                logger.LogInformation("Veritabanında restoran bulunamadı.");
                return;
            }

            logger.LogInformation($"{restaurants.Count} restoran bulundu, owner ID'leri güncelleniyor...");

            // Her restoranın owner ID'sini güncelle
            foreach (var restaurant in restaurants)
            {
                restaurant.OwnerId = owner.Id;
                logger.LogInformation($"✓ {restaurant.Name} -> Owner ID güncellendi");
            }

            await context.SaveChangesAsync();
            logger.LogInformation($"✅ {restaurants.Count} restoranın owner ID'si başarıyla güncellendi!");
        }
        catch (Exception ex)
        {
            logger.LogError(ex, "Owner ID güncellenirken hata oluştu!");
        }
    }
}
