using Microsoft.EntityFrameworkCore;
using Microsoft.EntityFrameworkCore.Metadata.Builders;
using RestaurantManagment.Domain.Models;

namespace RestaurantManagment.Persistance.Configurations;

public class JobPostingConfiguration : IEntityTypeConfiguration<JobPosting>
{
    public void Configure(EntityTypeBuilder<JobPosting> builder)
    {
        builder.HasKey(j => j.Id);

        builder.Property(j => j.Title)
            .IsRequired()
            .HasMaxLength(200);

        builder.Property(j => j.Description)
            .IsRequired()
            .HasMaxLength(2000);

        builder.Property(j => j.Requirements)
            .IsRequired()
            .HasMaxLength(1000);

        builder.Property(j => j.Position)
            .IsRequired()
            .HasMaxLength(100);

        builder.Property(j => j.EmploymentType)
            .IsRequired()
            .HasMaxLength(50);

        // Relationships
        builder.HasOne(j => j.Restaurant)
            .WithMany(r => r.JobPostings)
            .HasForeignKey(j => j.RestaurantId)
            .OnDelete(DeleteBehavior.Cascade);

        // Indexes
        builder.HasIndex(j => j.RestaurantId);
        builder.HasIndex(j => j.IsActive);

        // Soft Delete Query Filter
        builder.HasQueryFilter(j => !j.IsDeleted);
    }
}

using Microsoft.EntityFrameworkCore;
using Microsoft.AspNetCore.Identity;
using RestaurantManagment.Domain.Models;
using RestaurantManagment.Persistance.Data;

namespace RestaurantManagment.WebAPI;

public static class OwnerIdFixer
{
    public static async Task FixRestaurantOwnerIds(IServiceProvider services)
    {
        using var scope = services.CreateScope();
        var context = scope.ServiceProvider.GetRequiredService<AppDbContext>();
        var userManager = scope.ServiceProvider.GetRequiredService<UserManager<AppUser>>();
        var logger = scope.ServiceProvider.GetRequiredService<ILogger<Program>>();

        try
        {
            // Owner kullanıcısını bul
            var owner = await userManager.FindByEmailAsync("owner@gmail.com");
            if (owner == null)
            {
                logger.LogWarning("owner@gmail.com kullanıcısı bulunamadı!");
                return;
            }

            logger.LogInformation($"Owner bulundu: {owner.Email} (ID: {owner.Id})");

            // Tüm aktif restoranları bul
            var restaurants = await context.Restaurants
                .Where(r => !r.IsDeleted)
                .ToListAsync();

            if (restaurants.Count == 0)
            {
                logger.LogInformation("Veritabanında restoran bulunamadı.");
                return;
            }

            logger.LogInformation($"{restaurants.Count} restoran bulundu, owner ID'leri güncelleniyor...");

            // Her restoranın owner ID'sini güncelle
            foreach (var restaurant in restaurants)
            {
                restaurant.OwnerId = owner.Id;
                logger.LogInformation($"✓ {restaurant.Name} -> Owner ID güncellendi");
            }

            await context.SaveChangesAsync();
            logger.LogInformation($"✅ {restaurants.Count} restoranın owner ID'si başarıyla güncellendi!");
        }
        catch (Exception ex)
        {
            logger.LogError(ex, "Owner ID güncellenirken hata oluştu!");
        }
    }
}

-- Bu script tüm restoranların owner ID'sini owner@gmail.com kullanıcısına atar

DECLARE @OwnerId NVARCHAR(450);

-- Owner kullanıcısının ID'sini bul
SELECT @OwnerId = Id FROM AspNetUsers WHERE Email = 'owner@gmail.com';

IF @OwnerId IS NULL
BEGIN
    PRINT 'HATA: owner@gmail.com bulunamadı!';
    RETURN;
END

PRINT 'Owner ID bulundu: ' + @OwnerId;

-- Tüm restoranların owner ID'sini güncelle
UPDATE Restaurants 
SET OwnerId = @OwnerId
WHERE IsDeleted = 0;

-- Kaç restoran güncellendi
DECLARE @UpdatedCount INT = @@ROWCOUNT;
PRINT CAST(@UpdatedCount AS VARCHAR) + ' restoran güncellendi!';

-- Sonuçları göster
SELECT 
    Id,
    Name,
    OwnerId,
    Address,
    PhoneNumber
FROM Restaurants
WHERE IsDeleted = 0 AND OwnerId = @OwnerId;
