option.value:
"use client";

import { useState } from "react";
import { useRouter } from "next/navigation";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Textarea } from "@/components/ui/textarea";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { useToast } from "@/hooks/use-toast";
import { Building2, Loader2 } from "lucide-react";

export default function ApplyRestaurantOwnerPage() {
  const router = useRouter();
  const { toast } = useToast();
  const [loading, setLoading] = useState(false);
  const [formData, setFormData] = useState({
    businessName: "",
    businessDescription: "",
    businessAddress: "",
    businessPhone: "",
    businessEmail: "",
    category: "",
    additionalNotes: "",
  });

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    setLoading(true);

    try {
      const token = localStorage.getItem("token");
      if (!token) {
        toast({
          title: "Hata",
          description: "Lütfen önce giriş yapın",
          variant: "destructive",
        });
        router.push("/login");
        return;
      }

      const response = await fetch("http://localhost:5272/api/Account/restaurant-ownership-application", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${token}`,
        },
        body: JSON.stringify(formData),
      });

      const data = await response.json();

      if (response.ok) {
        toast({
          title: "Başarılı",
          description: data.message || "Başvurunuz alındı ve incelenecektir.",
        });
        router.push("/profile");
      } else {
        toast({
          title: "Hata",
          description: data.message || "Başvuru gönderilemedi",
          variant: "destructive",
        });
      }
    } catch (error) {
      console.error("Başvuru hatası:", error);
      toast({
        title: "Hata",
        description: "Bir hata oluştu. Lütfen tekrar deneyin.",
        variant: "destructive",
      });
    } finally {
      setLoading(false);
    }
  };

  const handleChange = (e: React.ChangeEvent<HTMLInputElement | HTMLTextAreaElement>) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value,
    });
  };

  return (
    <div className="container mx-auto px-4 py-8 max-w-3xl">
      <Card>
        <CardHeader>
          <div className="flex items-center gap-3">
            <Building2 className="h-8 w-8 text-primary" />
            <div>
              <CardTitle className="text-2xl">Restoran Sahipliği Başvurusu</CardTitle>
              <CardDescription>
                Restoranınızı platformumuza eklemek için başvuru formu
              </CardDescription>
            </div>
          </div>
        </CardHeader>
        <CardContent>
          <form onSubmit={handleSubmit} className="space-y-6">
            <div className="space-y-2">
              <Label htmlFor="businessName">İşletme Adı *</Label>
              <Input
                id="businessName"
                name="businessName"
                value={formData.businessName}
                onChange={handleChange}
                placeholder="Örn: Lezzet Durağı Restaurant"
                required
                maxLength={500}
              />
            </div>

            <div className="space-y-2">
              <Label htmlFor="businessDescription">İşletme Açıklaması *</Label>
              <Textarea
                id="businessDescription"
                name="businessDescription"
                value={formData.businessDescription}
                onChange={handleChange}
                placeholder="Restoranınız hakkında detaylı bilgi verin..."
                required
                maxLength={1000}
                rows={4}
              />
              <p className="text-sm text-muted-foreground">
                {formData.businessDescription.length}/1000 karakter
              </p>
            </div>

            <div className="space-y-2">
              <Label htmlFor="businessAddress">İşletme Adresi *</Label>
              <Textarea
                id="businessAddress"
                name="businessAddress"
                value={formData.businessAddress}
                onChange={handleChange}
                placeholder="Restoranınızın tam adresi..."
                required
                maxLength={500}
                rows={3}
              />
            </div>

            <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
              <div className="space-y-2">
                <Label htmlFor="businessPhone">İşletme Telefonu *</Label>
                <Input
                  id="businessPhone"
                  name="businessPhone"
                  type="tel"
                  value={formData.businessPhone}
                  onChange={handleChange}
                  placeholder="0555 555 55 55"
                  required
                  maxLength={20}
                />
              </div>

              <div className="space-y-2">
                <Label htmlFor="businessEmail">İşletme E-postası</Label>
                <Input
                  id="businessEmail"
                  name="businessEmail"
                  type="email"
                  value={formData.businessEmail}
                  onChange={handleChange}
                  placeholder="info@restaurant.com"
                  maxLength={100}
                />
              </div>
            </div>

            <div className="space-y-2">
              <Label htmlFor="category">Kategori *</Label>
              <Input
                id="category"
                name="category"
                value={formData.category}
                onChange={handleChange}
                placeholder="Örn: Türk Mutfağı, İtalyan, Fast Food"
                required
                maxLength={100}
              />
            </div>

            <div className="space-y-2">
              <Label htmlFor="additionalNotes">Ek Notlar</Label>
              <Textarea
                id="additionalNotes"
                name="additionalNotes"
                value={formData.additionalNotes}
                onChange={handleChange}
                placeholder="Eklemek istediğiniz diğer bilgiler..."
                maxLength={2000}
                rows={4}
              />
              <p className="text-sm text-muted-foreground">
                {formData.additionalNotes.length}/2000 karakter
              </p>
            </div>

            <div className="bg-muted p-4 rounded-lg">
              <p className="text-sm text-muted-foreground">
                * işaretli alanlar zorunludur. Başvurunuz incelendikten sonra size geri dönüş yapılacaktır.
              </p>
            </div>

            <div className="flex gap-4">
              <Button
                type="submit"
                className="flex-1"
                disabled={loading}
              >
                {loading ? (
                  <>
                    <Loader2 className="mr-2 h-4 w-4 animate-spin" />
                    Gönderiliyor...
                  </>
                ) : (
                  "Başvuruyu Gönder"
                )}
              </Button>
              <Button
                type="button"
                variant="outline"
                onClick={() => router.back()}
                disabled={loading}
              >
                İptal
              </Button>
            </div>
          </form>
        </CardContent>
      </Card>
    </div>
  );
}

"use client"

import { useState, useEffect } from "react"
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card"
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs"
import { Button } from "@/components/ui/button"
import { Badge } from "@/components/ui/badge"
import { Users, UtensilsCrossed, MessageSquare, DollarSign, ShoppingBag, Star, ChevronLeft, ChevronRight, Plus, Edit, Trash2, Check, X } from "lucide-react"
import { useToast } from "@/hooks/use-toast"
import { api } from "@/lib/api"
import { useRestaurant } from "@/contexts/restaurant-context"
import { Dialog, DialogContent, DialogDescription, DialogFooter, DialogHeader, DialogTitle, DialogTrigger } from "@/components/ui/dialog"
import { Input } from "@/components/ui/input"
import { Label } from "@/components/ui/label"
import { Textarea } from "@/components/ui/textarea"

interface DashboardStats {
  totalEmployees: number
  pendingEmployeeApplications: number
  totalMenuItems: number
  totalReviews: number
  pendingReviews: number
  averageRating: number
  totalOrders: number
  totalRevenue: number
}

interface Employee {
  id: string
  fullName: string
  email: string
  phoneNumber: string
  hireDate: string
  isActive: boolean
}

interface MenuItem {
  id: string
  name: string
  description: string
  price: number
  category: string
  isAvailable: boolean
  imageUrl?: string
}

interface Review {
  id: string
  customerName: string
  customerEmail: string
  rating: number
  comment: string
  status: string
  createdAt: string
  ownerResponse?: string
}

interface PaginatedResult<T> {
  items: T[]
  totalCount: number
  pageNumber: number
  pageSize: number
  totalPages: number
  hasPreviousPage: boolean
  hasNextPage: boolean
}

export default function OwnerDashboardPage() {
  const { selectedRestaurant } = useRestaurant()
  const [stats, setStats] = useState<DashboardStats | null>(null)
  const [employees, setEmployees] = useState<PaginatedResult<Employee> | null>(null)
  const [menuItems, setMenuItems] = useState<PaginatedResult<MenuItem> | null>(null)
  const [reviews, setReviews] = useState<PaginatedResult<Review> | null>(null)
  const [loading, setLoading] = useState(true)
  const [activeTab, setActiveTab] = useState("employees")
  const [employeePage, setEmployeePage] = useState(1)
  const [menuPage, setMenuPage] = useState(1)
  const [reviewPage, setReviewPage] = useState(1)
  const { toast } = useToast()

  // Employee form state
  const [employeeForm, setEmployeeForm] = useState({ fullName: "", email: "", password: "", phoneNumber: "" })
  const [editingEmployee, setEditingEmployee] = useState<Employee | null>(null)
  const [employeeDialogOpen, setEmployeeDialogOpen] = useState(false)

  // Menu form state
  const [menuForm, setMenuForm] = useState({ name: "", description: "", price: 0, category: "", isAvailable: true, imageUrl: "" })
  const [editingMenuItem, setEditingMenuItem] = useState<MenuItem | null>(null)
  const [menuDialogOpen, setMenuDialogOpen] = useState(false)

  useEffect(() => {
    if (selectedRestaurant) {
      fetchDashboardStats()
    }
  }, [selectedRestaurant])

  useEffect(() => {
    if (activeTab === "employees" && selectedRestaurant) fetchEmployees(employeePage)
  }, [activeTab, employeePage, selectedRestaurant])

  useEffect(() => {
    if (activeTab === "menu" && selectedRestaurant) fetchMenuItems(menuPage)
  }, [activeTab, menuPage, selectedRestaurant])

  useEffect(() => {
    if (activeTab === "reviews" && selectedRestaurant) fetchReviews(reviewPage)
  }, [activeTab, reviewPage, selectedRestaurant])

  const fetchDashboardStats = async () => {
    if (!selectedRestaurant) return
    try {
      setLoading(true)
      const response = await api.get<DashboardStats>(`/owner/dashboard?restaurantId=${selectedRestaurant.id}`)
      setStats(response)
    } catch (error: any) {
      toast({ title: "Hata", description: error.message || "Dashboard verileri yüklenemedi", variant: "destructive" })
    } finally {
      setLoading(false)
    }
  }

  const fetchEmployees = async (page: number) => {
    if (!selectedRestaurant) return
    try {
      const response = await api.get<PaginatedResult<Employee>>(`/owner/employees?restaurantId=${selectedRestaurant.id}&pageNumber=${page}&pageSize=10`)
      setEmployees(response)
    } catch (error: any) {
      toast({ title: "Hata", description: error.message || "Çalışanlar yüklenemedi", variant: "destructive" })
    }
  }

  const fetchMenuItems = async (page: number) => {
    if (!selectedRestaurant) return
    try {
      const response = await api.get<PaginatedResult<MenuItem>>(`/owner/menu-items?restaurantId=${selectedRestaurant.id}&pageNumber=${page}&pageSize=10`)
      setMenuItems(response)
    } catch (error: any) {
      toast({ title: "Hata", description: error.message || "Menü öğeleri yüklenemedi", variant: "destructive" })
    }
  }

  const fetchReviews = async (page: number) => {
    if (!selectedRestaurant) return
    try {
      const response = await api.get<PaginatedResult<Review>>(`/owner/reviews?restaurantId=${selectedRestaurant.id}&pageNumber=${page}&pageSize=10`)
      setReviews(response)
    } catch (error: any) {
      toast({ title: "Hata", description: error.message || "Yorumlar yüklenemedi", variant: "destructive" })
    }
  }

  // Employee CRUD
  const handleCreateEmployee = async () => {
    if (!selectedRestaurant) return
    try {
      await api.post(`/owner/employees?restaurantId=${selectedRestaurant.id}`, employeeForm)
      toast({ title: "Başarılı", description: "Çalışan oluşturuldu" })
      setEmployeeDialogOpen(false)
      setEmployeeForm({ fullName: "", email: "", password: "", phoneNumber: "" })
      fetchEmployees(employeePage)
      fetchDashboardStats()
    } catch (error: any) {
      toast({ title: "Hata", description: error.message || "Çalışan oluşturulamadı", variant: "destructive" })
    }
  }

  const handleUpdateEmployee = async () => {
    if (!editingEmployee || !selectedRestaurant) return
    try {
      await api.put(`/owner/employees/${editingEmployee.id}?restaurantId=${selectedRestaurant.id}`, {
        fullName: employeeForm.fullName,
        phoneNumber: employeeForm.phoneNumber,
        isActive: editingEmployee.isActive
      })
      toast({ title: "Başarılı", description: "Çalışan güncellendi" })
      setEmployeeDialogOpen(false)
      setEditingEmployee(null)
      setEmployeeForm({ fullName: "", email: "", password: "", phoneNumber: "" })
      fetchEmployees(employeePage)
    } catch (error: any) {
      toast({ title: "Hata", description: error.message || "Çalışan güncellenemedi", variant: "destructive" })
    }
  }

  const handleDeleteEmployee = async (id: string) => {
    if (!confirm("Bu çalışanı silmek istediğinizden emin misiniz?") || !selectedRestaurant) return
    try {
      await api.delete(`/owner/employees/${id}?restaurantId=${selectedRestaurant.id}`)
      toast({ title: "Başarılı", description: "Çalışan silindi" })
      fetchEmployees(employeePage)
      fetchDashboardStats()
    } catch (error: any) {
      toast({ title: "Hata", description: error.message || "Çalışan silinemedi", variant: "destructive" })
    }
  }

  // Menu CRUD
  const handleCreateMenuItem = async () => {
    if (!selectedRestaurant) return
    try {
      await api.post(`/owner/menu-items?restaurantId=${selectedRestaurant.id}`, menuForm)
      toast({ title: "Başarılı", description: "Menü öğesi oluşturuldu" })
      setMenuDialogOpen(false)
      setMenuForm({ name: "", description: "", price: 0, category: "", isAvailable: true, imageUrl: "" })
      fetchMenuItems(menuPage)
      fetchDashboardStats()
    } catch (error: any) {
      toast({ title: "Hata", description: error.message || "Menü öğesi oluşturulamadı", variant: "destructive" })
    }
  }

  const handleUpdateMenuItem = async () => {
    if (!editingMenuItem || !selectedRestaurant) return
    try {
      await api.put(`/owner/menu-items/${editingMenuItem.id}?restaurantId=${selectedRestaurant.id}`, menuForm)
      toast({ title: "Başarılı", description: "Menü öğesi güncellendi" })
      setMenuDialogOpen(false)
      setEditingMenuItem(null)
      setMenuForm({ name: "", description: "", price: 0, category: "", isAvailable: true, imageUrl: "" })
      fetchMenuItems(menuPage)
    } catch (error: any) {
      toast({ title: "Hata", description: error.message || "Menü öğesi güncellenemedi", variant: "destructive" })
    }
  }

  const handleDeleteMenuItem = async (id: string) => {
    if (!confirm("Bu menü öğesini silmek istediğinizden emin misiniz?") || !selectedRestaurant) return
    try {
      await api.delete(`/owner/menu-items/${id}?restaurantId=${selectedRestaurant.id}`)
      toast({ title: "Başarılı", description: "Menü öğesi silindi" })
      fetchMenuItems(menuPage)
      fetchDashboardStats()
    } catch (error: any) {
      toast({ title: "Hata", description: error.message || "Menü öğesi silinemedi", variant: "destructive" })
    }
  }

  // Review actions
  const handleApproveReview = async (id: string) => {
    if (!selectedRestaurant) return
    const response = prompt("Yanıt eklemek ister misiniz? (İsteğe bağlı)")
    try {
      await api.post(`/owner/reviews/${id}/approve?restaurantId=${selectedRestaurant.id}`, { response: response || undefined })
      toast({ title: "Başarılı", description: "Yorum onaylandı" })
      fetchReviews(reviewPage)
      fetchDashboardStats()
    } catch (error: any) {
      toast({ title: "Hata", description: error.message || "Yorum onaylanamadı", variant: "destructive" })
    }
  }

  const handleRejectReview = async (id: string) => {
    if (!selectedRestaurant) return
    const response = prompt("Red sebebini girin:")
    if (!response) return
    try {
      await api.post(`/owner/reviews/${id}/reject?restaurantId=${selectedRestaurant.id}`, { response })
      toast({ title: "Başarılı", description: "Yorum reddedildi" })
      fetchReviews(reviewPage)
      fetchDashboardStats()
    } catch (error: any) {
      toast({ title: "Hata", description: error.message || "Yorum reddedilemedi", variant: "destructive" })
    }
  }

  const PaginationControls = ({ data, currentPage, onPageChange }: { 
    data: PaginatedResult<any> | null, 
    currentPage: number, 
    onPageChange: (page: number) => void 
  }) => {
    if (!data || data.totalPages <= 1) return null
    return (
      <div className="flex items-center justify-between mt-4 pt-4 border-t">
        <div className="text-sm text-muted-foreground">
          Toplam {data.totalCount} kayıt - Sayfa {data.pageNumber} / {data.totalPages}
        </div>
        <div className="flex gap-2">
          <Button variant="outline" size="sm" onClick={() => onPageChange(currentPage - 1)} disabled={!data.hasPreviousPage}>
            <ChevronLeft className="h-4 w-4 mr-1" />Önceki
          </Button>
          <Button variant="outline" size="sm" onClick={() => onPageChange(currentPage + 1)} disabled={!data.hasNextPage}>
            Sonraki<ChevronRight className="h-4 w-4 ml-1" />
          </Button>
        </div>
      </div>
    )
  }

  if (!selectedRestaurant) {
    return (
      <div className="container py-8">
        <div className="flex flex-col items-center justify-center min-h-[400px]">
          <MessageSquare className="h-16 w-16 text-muted-foreground mb-4" />
          <h2 className="text-2xl font-semibold mb-2">Restoran Seçin</h2>
          <p className="text-muted-foreground text-center">
            Lütfen sol menüden bir restoran seçarak başlayın
          </p>
        </div>
      </div>
    )
  }

  if (loading) {
    return (
      <div className="container py-8">
        <div className="animate-pulse space-y-6">
          <div className="h-8 bg-muted rounded w-1/3" />
        </div>
      </div>
    )
  }

  const statsCards = stats ? [
    { title: "Toplam Çalışan", value: stats.totalEmployees, icon: Users, color: "text-blue-600" },
    { title: "Menü Öğeleri", value: stats.totalMenuItems, icon: UtensilsCrossed, color: "text-green-600" },
    { title: "Bekleyen Yorumlar", value: stats.pendingReviews, icon: MessageSquare, color: "text-orange-600" },
    { title: "Ortalama Puan", value: stats.averageRating.toFixed(1), icon: Star, color: "text-yellow-600" },
    { title: "Toplam Sipariş", value: stats.totalOrders, icon: ShoppingBag, color: "text-purple-600" },
    { title: "Toplam Gelir", value: `${stats.totalRevenue.toFixed(2)} ₺`, icon: DollarSign, color: "text-emerald-600" },
  ] : []

  return (
    <div className="container py-8">
      <div className="mb-8">
        <h1 className="text-4xl font-bold mb-2">Restoran Yönetim Paneli</h1>
        <p className="text-muted-foreground">Restoranınızı yönetin</p>
      </div>

      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6 mb-8">
        {statsCards.map((stat) => {
          const Icon = stat.icon
          return (
            <Card key={stat.title}>
              <CardHeader className="flex flex-row items-center justify-between pb-2">
                <CardTitle className="text-sm font-medium text-muted-foreground">{stat.title}</CardTitle>
                <Icon className={`h-5 w-5 ${stat.color}`} />
              </CardHeader>
              <CardContent><div className="text-2xl font-bold">{stat.value}</div></CardContent>
            </Card>
          )
        })}
      </div>

      <Tabs value={activeTab} onValueChange={setActiveTab} className="w-full">
        <TabsList className="mb-6">
          <TabsTrigger value="employees"><Users className="h-4 w-4 mr-2" />Çalışanlar</TabsTrigger>
          <TabsTrigger value="menu"><UtensilsCrossed className="h-4 w-4 mr-2" />Menü</TabsTrigger>
          <TabsTrigger value="reviews"><MessageSquare className="h-4 w-4 mr-2" />Yorumlar</TabsTrigger>
        </TabsList>

        <TabsContent value="employees">
          <Card>
            <CardHeader className="flex flex-row items-center justify-between">
              <div>
                <CardTitle>Çalışan Yönetimi</CardTitle>
                <CardDescription>Çalışanlarınızı görüntüleyin ve yönetin</CardDescription>
              </div>
              <Dialog open={employeeDialogOpen} onOpenChange={setEmployeeDialogOpen}>
                <DialogTrigger asChild>
                  <Button onClick={() => { setEditingEmployee(null); setEmployeeForm({ fullName: "", email: "", password: "", phoneNumber: "" }) }}>
                    <Plus className="h-4 w-4 mr-2" />Yeni Çalışan
                  </Button>
                </DialogTrigger>
                <DialogContent>
                  <DialogHeader>
                    <DialogTitle>{editingEmployee ? "Çalışan Düzenle" : "Yeni Çalışan Ekle"}</DialogTitle>
                  </DialogHeader>
                  <div className="space-y-4">
                    <div>
                      <Label>Ad Soyad</Label>
                      <Input value={employeeForm.fullName} onChange={(e) => setEmployeeForm({...employeeForm, fullName: e.target.value})} />
                    </div>
                    {!editingEmployee && (
                      <>
                        <div>
                          <Label>Email</Label>
                          <Input type="email" value={employeeForm.email} onChange={(e) => setEmployeeForm({...employeeForm, email: e.target.value})} />
                        </div>
                        <div>
                          <Label>Şifre</Label>
                          <Input type="password" value={employeeForm.password} onChange={(e) => setEmployeeForm({...employeeForm, password: e.target.value})} />
                        </div>
                      </>
                    )}
                    <div>
                      <Label>Telefon</Label>
                      <Input value={employeeForm.phoneNumber} onChange={(e) => setEmployeeForm({...employeeForm, phoneNumber: e.target.value})} />
                    </div>
                  </div>
                  <DialogFooter>
                    <Button onClick={editingEmployee ? handleUpdateEmployee : handleCreateEmployee}>
                      {editingEmployee ? "Güncelle" : "Oluştur"}
                    </Button>
                  </DialogFooter>
                </DialogContent>
              </Dialog>
            </CardHeader>
            <CardContent>
              <div className="space-y-4">
                {employees?.items.map((employee) => (
                  <div key={employee.id} className="flex items-center justify-between p-4 border rounded-lg">
                    <div className="flex-1">
                      <div className="flex items-center gap-3 mb-2">
                        <h3 className="font-semibold">{employee.fullName}</h3>
                        <Badge variant={employee.isActive ? "default" : "secondary"}>
                          {employee.isActive ? "Aktif" : "Pasif"}
                        </Badge>
                      </div>
                      <p className="text-sm text-muted-foreground">{employee.email}</p>
                      <p className="text-sm text-muted-foreground">{employee.phoneNumber}</p>
                    </div>
                    <div className="flex gap-2">
                      <Button size="sm" variant="outline" onClick={() => { 
                        setEditingEmployee(employee)
                        setEmployeeForm({ fullName: employee.fullName, email: employee.email, password: "", phoneNumber: employee.phoneNumber })
                        setEmployeeDialogOpen(true)
                      }}>
                        <Edit className="h-4 w-4" />
                      </Button>
                      <Button size="sm" variant="destructive" onClick={() => handleDeleteEmployee(employee.id)}>
                        <Trash2 className="h-4 w-4" />
                      </Button>
                    </div>
                  </div>
                ))}
              </div>
              <PaginationControls data={employees} currentPage={employeePage} onPageChange={setEmployeePage} />
            </CardContent>
          </Card>
        </TabsContent>

        <TabsContent value="menu">
          <Card>
            <CardHeader className="flex flex-row items-center justify-between">
              <div>
                <CardTitle>Menü Yönetimi</CardTitle>
                <CardDescription>Menü öğelerinizi görüntüleyin ve yönetin</CardDescription>
              </div>
              <Dialog open={menuDialogOpen} onOpenChange={setMenuDialogOpen}>
                <DialogTrigger asChild>
                  <Button onClick={() => { setEditingMenuItem(null); setMenuForm({ name: "", description: "", price: 0, category: "", isAvailable: true, imageUrl: "" }) }}>
                    <Plus className="h-4 w-4 mr-2" />Yeni Menü Öğesi
                  </Button>
                </DialogTrigger>
                <DialogContent>
                  <DialogHeader>
                    <DialogTitle>{editingMenuItem ? "Menü Düzenle" : "Yeni Menü Öğesi Ekle"}</DialogTitle>
                  </DialogHeader>
                  <div className="space-y-4">
                    <div>
                      <Label>Adı</Label>
                      <Input value={menuForm.name} onChange={(e) => setMenuForm({...menuForm, name: e.target.value})} />
                    </div>
                    <div>
                      <Label>Açıklama</Label>
                      <Textarea value={menuForm.description} onChange={(e) => setMenuForm({...menuForm, description: e.target.value})} />
                    </div>
                    <div>
                      <Label>Fiyat (₺)</Label>
                      <Input type="number" step="0.01" value={menuForm.price} onChange={(e) => setMenuForm({...menuForm, price: parseFloat(e.target.value)})} />
                    </div>
                    <div>
                      <Label>Kategori</Label>
                      <Input value={menuForm.category} onChange={(e) => setMenuForm({...menuForm, category: e.target.value})} />
                    </div>
                    <div>
                      <Label>Resim URL (isteğe bağlı)</Label>
                      <Input value={menuForm.imageUrl} onChange={(e) => setMenuForm({...menuForm, imageUrl: e.target.value})} />
                    </div>
                  </div>
                  <DialogFooter>
                    <Button onClick={editingMenuItem ? handleUpdateMenuItem : handleCreateMenuItem}>
                      {editingMenuItem ? "Güncelle" : "Oluştur"}
                    </Button>
                  </DialogFooter>
                </DialogContent>
              </Dialog>
            </CardHeader>
            <CardContent>
              <div className="space-y-4">
                {menuItems?.items.map((item) => (
                  <div key={item.id} className="flex items-start justify-between p-4 border rounded-lg">
                    <div className="flex-1">
                      <div className="flex items-center gap-3 mb-2">
                        <h3 className="font-semibold">{item.name}</h3>
                        <Badge variant={item.isAvailable ? "default" : "secondary"}>
                          {item.isAvailable ? "Mevcut" : "Tükendi"}
                        </Badge>
                        <Badge variant="outline">{item.category}</Badge>
                      </div>
                      <p className="text-sm text-muted-foreground mb-2">{item.description}</p>
                      <p className="text-lg font-bold text-primary">{item.price.toFixed(2)} ₺</p>
                    </div>
                    <div className="flex gap-2">
                      <Button size="sm" variant="outline" onClick={() => { 
                        setEditingMenuItem(item)
                        setMenuForm({
                          name: item.name,
                          description: item.description,
                          price: item.price,
                          category: item.category,
                          isAvailable: item.isAvailable,
                          imageUrl: item.imageUrl || ""
                        })
                        setMenuDialogOpen(true)
                      }}>
                        <Edit className="h-4 w-4" />
                      </Button>
                      <Button size="sm" variant="destructive" onClick={() => handleDeleteMenuItem(item.id)}>
                        <Trash2 className="h-4 w-4" />
                      </Button>
                    </div>
                  </div>
                ))}
              </div>
              <PaginationControls data={menuItems} currentPage={menuPage} onPageChange={setMenuPage} />
            </CardContent>
          </Card>
        </TabsContent>

        <TabsContent value="reviews">
          <Card>
            <CardHeader>
              <CardTitle>Yorum Yönetimi</CardTitle>
              <CardDescription>Müşteri yorumlarını görüntüleyin ve yönetin</CardDescription>
            </CardHeader>
            <CardContent>
              <div className="space-y-4">
                {reviews?.items.map((review) => (
                  <div key={review.id} className="p-4 border rounded-lg">
                    <div className="flex items-start justify-between mb-3">
                      <div>
                        <h3 className="font-semibold">{review.customerName}</h3>
                        <p className="text-sm text-muted-foreground">{review.customerEmail}</p>
                      </div>
                      <div className="flex items-center gap-2">
                        <Badge variant={review.status === "Approved" ? "default" : review.status === "Rejected" ? "destructive" : "secondary"}>
                          {review.status === "Approved" ? "Onaylandı" : review.status === "Rejected" ? "Reddedildi" : "Beklemede"}
                        </Badge>
                        <div className="flex items-center">
                          <Star className="h-4 w-4 fill-yellow-400 text-yellow-400 mr-1" />
                          <span className="font-semibold">{review.rating}</span>
                        </div>
                      </div>
                    </div>
                    <p className="text-sm mb-3">{review.comment}</p>
                    {review.ownerResponse && (
                      <div className="bg-muted p-3 rounded-md mb-3">
                        <p className="text-sm"><strong>Yanıtınız:</strong> {review.ownerResponse}</p>
                      </div>
                    )}
                    <div className="flex items-center justify-between">
                      <p className="text-xs text-muted-foreground">{new Date(review.createdAt).toLocaleDateString('tr-TR')}</p>
                      {review.status === "Pending" && (
                        <div className="flex gap-2">
                          <Button size="sm" variant="default" onClick={() => handleApproveReview(review.id)}>
                            <Check className="h-4 w-4 mr-1" />Onayla
                          </Button>
                          <Button size="sm" variant="destructive" onClick={() => handleRejectReview(review.id)}>
                            <X className="h-4 w-4 mr-1" />Reddet
                          </Button>
                        </div>
                      )}
                    </div>
                  </div>
                ))}
              </div>
              <PaginationControls data={reviews} currentPage={reviewPage} onPageChange={setReviewPage} />
            </CardContent>
          </Card>
        </TabsContent>
      </Tabs>
    </div>
  )
}

"use client"

import { useState, useEffect } from "react"
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card"
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs"
import { Button } from "@/components/ui/button"
import { Badge } from "@/components/ui/badge"
import { 
  Users, 
  DollarSign, 
  ShoppingBag, 
  Star, 
  UserPlus, 
  MessageSquare,
  TrendingUp,
  Calendar,
  CheckCircle,
  XCircle,
  Edit,
  Trash2,
  Plus,
  Eye
} from "lucide-react"
import { useToast } from "@/hooks/use-toast"
import { useRestaurant } from "@/contexts/restaurant-context"
import { 
  employeeApi, 
  jobApplicationApi, 
  reviewApi, 
  orderApi,
  reservationApi 
} from "@/lib/api"
import {
  Dialog,
  DialogContent,
  DialogDescription,
  DialogFooter,
  DialogHeader,
  DialogTitle,
} from "@/components/ui/dialog"
import { Input } from "@/components/ui/input"
import { Label } from "@/components/ui/label"
import { Textarea } from "@/components/ui/textarea"
import { 
  AppUser, 
  JobApplication, 
  Review, 
  Order, 
  Reservation,
  JobApplicationStatus,
  ReviewStatus,
  OrderStatus,
  ReservationStatus 
} from "@/types"
import {
  Table,
  TableBody,
  TableCell,
  TableHead,
  TableHeader,
  TableRow,
} from "@/components/ui/table"
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from "@/components/ui/select"

interface DashboardStats {
  totalRevenue: number
  todayRevenue: number
  totalOrders: number
  todayOrders: number
  totalEmployees: number
  pendingApplications: number
  pendingReviews: number
  activeReservations: number
}

export default function OwnerDashboardPage() {
  const { selectedRestaurant } = useRestaurant()
  const { toast } = useToast()

  const [stats, setStats] = useState<DashboardStats>({
    totalRevenue: 0,
    todayRevenue: 0,
    totalOrders: 0,
    todayOrders: 0,
    totalEmployees: 0,
    pendingApplications: 0,
    pendingReviews: 0,
    activeReservations: 0,
  })

  const [employees, setEmployees] = useState<AppUser[]>([])
  const [jobApplications, setJobApplications] = useState<JobApplication[]>([])
  const [reviews, setReviews] = useState<Review[]>([])
  const [orders, setOrders] = useState<Order[]>([])
  const [reservations, setReservations] = useState<Reservation[]>([])
  const [loading, setLoading] = useState(true)

  // Dialog states
  const [employeeDialogOpen, setEmployeeDialogOpen] = useState(false)
  const [editingEmployee, setEditingEmployee] = useState<AppUser | null>(null)
  const [employeeForm, setEmployeeForm] = useState({
    fullName: "",
    email: "",
    phoneNumber: "",
    password: "",
  })

  const [reviewDialogOpen, setReviewDialogOpen] = useState(false)
  const [selectedReview, setSelectedReview] = useState<Review | null>(null)
  const [reviewResponse, setReviewResponse] = useState("")

  const [applicationDialogOpen, setApplicationDialogOpen] = useState(false)
  const [selectedApplication, setSelectedApplication] = useState<JobApplication | null>(null)
  const [rejectionReason, setRejectionReason] = useState("")

  useEffect(() => {
    if (selectedRestaurant) {
      loadAllData()
    }
  }, [selectedRestaurant])

  const loadAllData = async () => {
    if (!selectedRestaurant) return

    setLoading(true)
    try {
      await Promise.all([
        loadEmployees(),
        loadJobApplications(),
        loadReviews(),
        loadOrders(),
        loadReservations(),
      ])
    } catch (error: any) {
      toast({
        title: "Hata",
        description: error.message || "Veriler yüklenirken bir hata oluştu",
        variant: "destructive",
      })
    } finally {
      setLoading(false)
    }
  }

  const loadEmployees = async () => {
    if (!selectedRestaurant) return
    try {
      const data = await employeeApi.getByRestaurant(selectedRestaurant.id)
      setEmployees(data)
      updateStats({ totalEmployees: data.length })
    } catch (error: any) {
      console.error("Error loading employees:", error)
    }
  }

  const loadJobApplications = async () => {
    if (!selectedRestaurant) return
    try {
      const data = await jobApplicationApi.getByRestaurant(selectedRestaurant.id)
      setJobApplications(data)
      const pending = data.filter((app: JobApplication) => app.status === JobApplicationStatus.Pending).length
      updateStats({ pendingApplications: pending })
    } catch (error: any) {
      console.error("Error loading job applications:", error)
    }
  }

  const loadReviews = async () => {
    if (!selectedRestaurant) return
    try {
      const data = await reviewApi.getByRestaurant(selectedRestaurant.id)
      setReviews(data)
      const pending = data.filter((review: Review) => review.status === ReviewStatus.Pending).length
      updateStats({ pendingReviews: pending })
    } catch (error: any) {
      console.error("Error loading reviews:", error)
    }
  }

  const loadOrders = async () => {
    if (!selectedRestaurant) return
    try {
      const allOrders = await orderApi.getAll()
      const restaurantOrders = allOrders.filter((order: Order) => order.restaurantId === selectedRestaurant.id)
      setOrders(restaurantOrders)

      const total = restaurantOrders.reduce((sum: number, order: Order) => sum + order.totalAmount, 0)
      const today = new Date().toDateString()
      const todayOrders = restaurantOrders.filter((order: Order) => 
        new Date(order.orderDate).toDateString() === today
      )
      const todayTotal = todayOrders.reduce((sum: number, order: Order) => sum + order.totalAmount, 0)

      updateStats({ 
        totalRevenue: total,
        todayRevenue: todayTotal,
        totalOrders: restaurantOrders.length,
        todayOrders: todayOrders.length,
      })
    } catch (error: any) {
      console.error("Error loading orders:", error)
    }
  }

  const loadReservations = async () => {
    if (!selectedRestaurant) return
    try {
      const allReservations = await reservationApi.getAll()
      const restaurantReservations = allReservations.filter((res: Reservation) => res.restaurantId === selectedRestaurant.id)
      setReservations(restaurantReservations)

      const active = restaurantReservations.filter((res: Reservation) => 
        res.status === ReservationStatus.Confirmed || res.status === ReservationStatus.Pending
      ).length
      updateStats({ activeReservations: active })
    } catch (error: any) {
      console.error("Error loading reservations:", error)
    }
  }

  const updateStats = (newStats: Partial<DashboardStats>) => {
    setStats(prev => ({ ...prev, ...newStats }))
  }

  // Employee CRUD operations
  const handleSaveEmployee = async () => {
    if (!selectedRestaurant) return

    try {
      if (editingEmployee) {
        await employeeApi.update(selectedRestaurant.id, editingEmployee.id, employeeForm)
        toast({
          title: "Başarılı",
          description: "Çalışan güncellendi",
        })
      } else {
        await employeeApi.create(selectedRestaurant.id, employeeForm)
        toast({
          title: "Başarılı",
          description: "Çalışan eklendi",
        })
      }

      setEmployeeDialogOpen(false)
      setEditingEmployee(null)
      setEmployeeForm({ fullName: "", email: "", phoneNumber: "", password: "" })
      await loadEmployees()
    } catch (error: any) {
      toast({
        title: "Hata",
        description: error.message || "İşlem başarısız",
        variant: "destructive",
      })
    }
  }

  const handleDeleteEmployee = async (employeeId: string) => {
    if (!selectedRestaurant || !confirm("Bu çalışanı silmek istediğinizden emin misiniz?")) return

    try {
      await employeeApi.delete(selectedRestaurant.id, employeeId)
      toast({
        title: "Başarılı",
        description: "Çalışan silindi",
      })
      await loadEmployees()
    } catch (error: any) {
      toast({
        title: "Hata",
        description: error.message || "Silme işlemi başarısız",
        variant: "destructive",
      })
    }
  }

  const handleEditEmployee = (employee: AppUser) => {
    setEditingEmployee(employee)
    setEmployeeForm({
      fullName: employee.fullName,
      email: employee.email,
      phoneNumber: employee.phoneNumber || "",
      password: "",
    })
    setEmployeeDialogOpen(true)
  }

  // Job Application operations
  const handleAcceptApplication = async (applicationId: string) => {
    try {
      await jobApplicationApi.accept(applicationId)
      toast({
        title: "Başarılı",
        description: "Başvuru kabul edildi",
      })
      setApplicationDialogOpen(false)
      await loadJobApplications()
    } catch (error: any) {
      toast({
        title: "Hata",
        description: error.message || "İşlem başarısız",
        variant: "destructive",
      })
    }
  }

  const handleRejectApplication = async (applicationId: string) => {
    try {
      await jobApplicationApi.reject(applicationId, rejectionReason)
      toast({
        title: "Başarılı",
        description: "Başvuru reddedildi",
      })
      setApplicationDialogOpen(false)
      setRejectionReason("")
      await loadJobApplications()
    } catch (error: any) {
      toast({
        title: "Hata",
        description: error.message || "İşlem başarısız",
        variant: "destructive",
      })
    }
  }

  // Review operations
  const handleApproveReview = async (reviewId: string) => {
    try {
      await reviewApi.approve(reviewId)
      toast({
        title: "Başarılı",
        description: "Yorum onaylandı",
      })
      await loadReviews()
    } catch (error: any) {
      toast({
        title: "Hata",
        description: error.message || "İşlem başarısız",
        variant: "destructive",
      })
    }
  }

  const handleRejectReview = async (reviewId: string) => {
    try {
      await reviewApi.reject(reviewId)
      toast({
        title: "Başarılı",
        description: "Yorum reddedildi",
      })
      await loadReviews()
    } catch (error: any) {
      toast({
        title: "Hata",
        description: error.message || "İşlem başarısız",
        variant: "destructive",
      })
    }
  }

  const handleRespondToReview = async () => {
    if (!selectedReview) return

    try {
      await reviewApi.respond(selectedReview.id, reviewResponse)
      toast({
        title: "Başarılı",
        description: "Yanıt gönderildi",
      })
      setReviewDialogOpen(false)
      setSelectedReview(null)
      setReviewResponse("")
      await loadReviews()
    } catch (error: any) {
      toast({
        title: "Hata",
        description: error.message || "İşlem başarısız",
        variant: "destructive",
      })
    }
  }

  if (!selectedRestaurant) {
    return (
      <div className="flex items-center justify-center min-h-screen">
        <Card className="w-96">
          <CardHeader>
            <CardTitle>Restoran Seçin</CardTitle>
            <CardDescription>
              Devam etmek için lütfen bir restoran seçin
            </CardDescription>
          </CardHeader>
        </Card>
      </div>
    )
  }

  if (loading) {
    return (
      <div className="flex items-center justify-center min-h-screen">
        <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-primary" />
      </div>
    )
  }

  return (
    <div className="container mx-auto p-6 space-y-6">
      <div className="flex items-center justify-between">
        <div>
          <h1 className="text-3xl font-bold">{selectedRestaurant.name}</h1>
          <p className="text-muted-foreground">Restoran Yönetim Paneli</p>
        </div>
      </div>

      {/* Stats Cards */}
      <div className="grid gap-4 md:grid-cols-2 lg:grid-cols-4">
        <Card>
          <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
            <CardTitle className="text-sm font-medium">Toplam Gelir</CardTitle>
            <DollarSign className="h-4 w-4 text-muted-foreground" />
          </CardHeader>
          <CardContent>
            <div className="text-2xl font-bold">₺{stats.totalRevenue.toFixed(2)}</div>
            <p className="text-xs text-muted-foreground">
              Bugün: ₺{stats.todayRevenue.toFixed(2)}
            </p>
          </CardContent>
        </Card>

        <Card>
          <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
            <CardTitle className="text-sm font-medium">Siparişler</CardTitle>
            <ShoppingBag className="h-4 w-4 text-muted-foreground" />
          </CardHeader>
          <CardContent>
            <div className="text-2xl font-bold">{stats.totalOrders}</div>
            <p className="text-xs text-muted-foreground">
              Bugün: {stats.todayOrders}
            </p>
          </CardContent>
        </Card>

        <Card>
          <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
            <CardTitle className="text-sm font-medium">Çalışanlar</CardTitle>
            <Users className="h-4 w-4 text-muted-foreground" />
          </CardHeader>
          <CardContent>
            <div className="text-2xl font-bold">{stats.totalEmployees}</div>
            <p className="text-xs text-muted-foreground">
              Bekleyen başvuru: {stats.pendingApplications}
            </p>
          </CardContent>
        </Card>

        <Card>
          <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
            <CardTitle className="text-sm font-medium">Rezervasyonlar</CardTitle>
            <Calendar className="h-4 w-4 text-muted-foreground" />
          </CardHeader>
          <CardContent>
            <div className="text-2xl font-bold">{stats.activeReservations}</div>
            <p className="text-xs text-muted-foreground">
              Bekleyen yorum: {stats.pendingReviews}
            </p>
          </CardContent>
        </Card>
      </div>

      {/* Tabs for different sections */}
      <Tabs defaultValue="employees" className="space-y-4">
        <TabsList>
          <TabsTrigger value="employees">
            Çalışanlar ({employees.length})
          </TabsTrigger>
          <TabsTrigger value="applications">
            İş Başvuruları ({jobApplications.length})
          </TabsTrigger>
          <TabsTrigger value="reviews">
            Yorumlar ({reviews.length})
          </TabsTrigger>
          <TabsTrigger value="orders">
            Siparişler ({orders.length})
          </TabsTrigger>
          <TabsTrigger value="reservations">
            Rezervasyonlar ({reservations.length})
          </TabsTrigger>
        </TabsList>

        {/* Employees Tab */}
        <TabsContent value="employees" className="space-y-4">
          <Card>
            <CardHeader>
              <div className="flex items-center justify-between">
                <div>
                  <CardTitle>Çalışanlar</CardTitle>
                  <CardDescription>Restoranınızda çalışan personeli yönetin</CardDescription>
                </div>
                <Button onClick={() => {
                  setEditingEmployee(null)
                  setEmployeeForm({ fullName: "", email: "", phoneNumber: "", password: "" })
                  setEmployeeDialogOpen(true)
                }}>
                  <Plus className="h-4 w-4 mr-2" />
                  Yeni Çalışan
                </Button>
              </div>
            </CardHeader>
            <CardContent>
              <Table>
                <TableHeader>
                  <TableRow>
                    <TableHead>Ad Soyad</TableHead>
                    <TableHead>Email</TableHead>
                    <TableHead>Telefon</TableHead>
                    <TableHead>Kayıt Tarihi</TableHead>
                    <TableHead className="text-right">İşlemler</TableHead>
                  </TableRow>
                </TableHeader>
                <TableBody>
                  {employees.length === 0 ? (
                    <TableRow>
                      <TableCell colSpan={5} className="text-center text-muted-foreground">
                        Henüz çalışan bulunmuyor
                      </TableCell>
                    </TableRow>
                  ) : (
                    employees.map((employee) => (
                      <TableRow key={employee.id}>
                        <TableCell className="font-medium">{employee.fullName}</TableCell>
                        <TableCell>{employee.email}</TableCell>
                        <TableCell>{employee.phoneNumber || "-"}</TableCell>
                        <TableCell>{new Date(employee.createdAt).toLocaleDateString('tr-TR')}</TableCell>
                        <TableCell className="text-right">
                          <div className="flex justify-end gap-2">
                            <Button
                              variant="outline"
                              size="sm"
                              onClick={() => handleEditEmployee(employee)}
                            >
                              <Edit className="h-4 w-4" />
                            </Button>
                            <Button
                              variant="destructive"
                              size="sm"
                              onClick={() => handleDeleteEmployee(employee.id)}
                            >
                              <Trash2 className="h-4 w-4" />
                            </Button>
                          </div>
                        </TableCell>
                      </TableRow>
                    ))
                  )}
                </TableBody>
              </Table>
            </CardContent>
          </Card>
        </TabsContent>

        {/* Job Applications Tab */}
        <TabsContent value="applications" className="space-y-4">
          <Card>
            <CardHeader>
              <CardTitle>İş Başvuruları</CardTitle>
              <CardDescription>Restoranınıza yapılan iş başvurularını inceleyin</CardDescription>
            </CardHeader>
            <CardContent>
              <Table>
                <TableHeader>
                  <TableRow>
                    <TableHead>Başvuran</TableHead>
                    <TableHead>Email</TableHead>
                    <TableHead>Telefon</TableHead>
                    <TableHead>Başvuru Tarihi</TableHead>
                    <TableHead>Durum</TableHead>
                    <TableHead className="text-right">İşlemler</TableHead>
                  </TableRow>
                </TableHeader>
                <TableBody>
                  {jobApplications.length === 0 ? (
                    <TableRow>
                      <TableCell colSpan={6} className="text-center text-muted-foreground">
                        Henüz başvuru bulunmuyor
                      </TableCell>
                    </TableRow>
                  ) : (
                    jobApplications.map((application) => (
                      <TableRow key={application.id}>
                        <TableCell className="font-medium">{application.applicantName}</TableCell>
                        <TableCell>{application.applicantEmail}</TableCell>
                        <TableCell>{application.applicantPhone || "-"}</TableCell>
                        <TableCell>{new Date(application.appliedDate).toLocaleDateString('tr-TR')}</TableCell>
                        <TableCell>
                          <Badge
                            variant={
                              application.status === JobApplicationStatus.Pending
                                ? "secondary"
                                : application.status === JobApplicationStatus.Accepted
                                ? "default"
                                : "destructive"
                            }
                          >
                            {application.status === JobApplicationStatus.Pending && "Beklemede"}
                            {application.status === JobApplicationStatus.Accepted && "Kabul Edildi"}
                            {application.status === JobApplicationStatus.Rejected && "Reddedildi"}
                            {application.status === JobApplicationStatus.Reviewed && "İncelendi"}
                          </Badge>
                        </TableCell>
                        <TableCell className="text-right">
                          <div className="flex justify-end gap-2">
                            <Button
                              variant="outline"
                              size="sm"
                              onClick={() => {
                                setSelectedApplication(application)
                                setApplicationDialogOpen(true)
                              }}
                            >
                              <Eye className="h-4 w-4" />
                            </Button>
                            {application.status === JobApplicationStatus.Pending && (
                              <>
                                <Button
                                  variant="default"
                                  size="sm"
                                  onClick={() => handleAcceptApplication(application.id)}
                                >
                                  <CheckCircle className="h-4 w-4" />
                                </Button>
                                <Button
                                  variant="destructive"
                                  size="sm"
                                  onClick={() => {
                                    setSelectedApplication(application)
                                    setApplicationDialogOpen(true)
                                  }}
                                >
                                  <XCircle className="h-4 w-4" />
                                </Button>
                              </>
                            )}
                          </div>
                        </TableCell>
                      </TableRow>
                    ))
                  )}
                </TableBody>
              </Table>
            </CardContent>
          </Card>
        </TabsContent>

        {/* Reviews Tab */}
        <TabsContent value="reviews" className="space-y-4">
          <Card>
            <CardHeader>
              <CardTitle>Yorumlar</CardTitle>
              <CardDescription>Restoranınıza gelen yorumları yönetin</CardDescription>
            </CardHeader>
            <CardContent>
              <Table>
                <TableHeader>
                  <TableRow>
                    <TableHead>Müşteri</TableHead>
                    <TableHead>Puan</TableHead>
                    <TableHead>Yorum</TableHead>
                    <TableHead>Tarih</TableHead>
                    <TableHead>Durum</TableHead>
                    <TableHead className="text-right">İşlemler</TableHead>
                  </TableRow>
                </TableHeader>
                <TableBody>
                  {reviews.length === 0 ? (
                    <TableRow>
                      <TableCell colSpan={6} className="text-center text-muted-foreground">
                        Henüz yorum bulunmuyor
                      </TableCell>
                    </TableRow>
                  ) : (
                    reviews.map((review) => (
                      <TableRow key={review.id}>
                        <TableCell className="font-medium">{review.customerName}</TableCell>
                        <TableCell>
                          <div className="flex items-center gap-1">
                            <Star className="h-4 w-4 fill-yellow-400 text-yellow-400" />
                            <span>{review.rating}</span>
                          </div>
                        </TableCell>
                        <TableCell className="max-w-xs truncate">{review.comment}</TableCell>
                        <TableCell>{new Date(review.createdAt).toLocaleDateString('tr-TR')}</TableCell>
                        <TableCell>
                          <Badge
                            variant={
                              review.status === ReviewStatus.Pending
                                ? "secondary"
                                : review.status === ReviewStatus.Approved
                                ? "default"
                                : "destructive"
                            }
                          >
                            {review.status === ReviewStatus.Pending && "Beklemede"}
                            {review.status === ReviewStatus.Approved && "Onaylandı"}
                            {review.status === ReviewStatus.Rejected && "Reddedildi"}
                          </Badge>
                        </TableCell>
                        <TableCell className="text-right">
                          <div className="flex justify-end gap-2">
                            {review.status === ReviewStatus.Pending && (
                              <>
                                <Button
                                  variant="default"
                                  size="sm"
                                  onClick={() => handleApproveReview(review.id)}
                                >
                                  <CheckCircle className="h-4 w-4" />
                                </Button>
                                <Button
                                  variant="destructive"
                                  size="sm"
                                  onClick={() => handleRejectReview(review.id)}
                                >
                                  <XCircle className="h-4 w-4" />
                                </Button>
                              </>
                            )}
                            <Button
                              variant="outline"
                              size="sm"
                              onClick={() => {
                                setSelectedReview(review)
                                setReviewResponse(review.adminResponse || "")
                                setReviewDialogOpen(true)
                              }}
                            >
                              <MessageSquare className="h-4 w-4" />
                            </Button>
                          </div>
                        </TableCell>
                      </TableRow>
                    ))
                  )}
                </TableBody>
              </Table>
            </CardContent>
          </Card>
        </TabsContent>

        {/* Orders Tab */}
        <TabsContent value="orders" className="space-y-4">
          <Card>
            <CardHeader>
              <CardTitle>Siparişler</CardTitle>
              <CardDescription>Restoranınızın siparişlerini görüntüleyin</CardDescription>
            </CardHeader>
            <CardContent>
              <Table>
                <TableHeader>
                  <TableRow>
                    <TableHead>Sipariş No</TableHead>
                    <TableHead>Tarih</TableHead>
                    <TableHead>Tutar</TableHead>
                    <TableHead>Tip</TableHead>
                    <TableHead>Durum</TableHead>
                  </TableRow>
                </TableHeader>
                <TableBody>
                  {orders.length === 0 ? (
                    <TableRow>
                      <TableCell colSpan={5} className="text-center text-muted-foreground">
                        Henüz sipariş bulunmuyor
                      </TableCell>
                    </TableRow>
                  ) : (
                    orders.slice(0, 10).map((order) => (
                      <TableRow key={order.id}>
                        <TableCell className="font-medium">#{order.id.slice(0, 8)}</TableCell>
                        <TableCell>{new Date(order.orderDate).toLocaleDateString('tr-TR')}</TableCell>
                        <TableCell>₺{order.totalAmount.toFixed(2)}</TableCell>
                        <TableCell>{order.type}</TableCell>
                        <TableCell>
                          <Badge>{order.status}</Badge>
                        </TableCell>
                      </TableRow>
                    ))
                  )}
                </TableBody>
              </Table>
            </CardContent>
          </Card>
        </TabsContent>

        {/* Reservations Tab */}
        <TabsContent value="reservations" className="space-y-4">
          <Card>
            <CardHeader>
              <CardTitle>Rezervasyonlar</CardTitle>
              <CardDescription>Restoranınızın rezervasyonlarını görüntüleyin</CardDescription>
            </CardHeader>
            <CardContent>
              <Table>
                <TableHeader>
                  <TableRow>
                    <TableHead>Müşteri</TableHead>
                    <TableHead>Telefon</TableHead>
                    <TableHead>Tarih</TableHead>
                    <TableHead>Kişi Sayısı</TableHead>
                    <TableHead>Durum</TableHead>
                  </TableRow>
                </TableHeader>
                <TableBody>
                  {reservations.length === 0 ? (
                    <TableRow>
                      <TableCell colSpan={5} className="text-center text-muted-foreground">
                        Henüz rezervasyon bulunmuyor
                      </TableCell>
                    </TableRow>
                  ) : (
                    reservations.map((reservation) => (
                      <TableRow key={reservation.id}>
                        <TableCell className="font-medium">{reservation.customerName}</TableCell>
                        <TableCell>{reservation.customerPhone}</TableCell>
                        <TableCell>{new Date(reservation.reservationDate).toLocaleDateString('tr-TR')}</TableCell>
                        <TableCell>{reservation.numberOfGuests}</TableCell>
                        <TableCell>
                          <Badge
                            variant={
                              reservation.status === ReservationStatus.Confirmed
                                ? "default"
                                : reservation.status === ReservationStatus.Pending
                                ? "secondary"
                                : "destructive"
                            }
                          >
                            {reservation.status === ReservationStatus.Pending && "Beklemede"}
                            {reservation.status === ReservationStatus.Confirmed && "Onaylandı"}
                            {reservation.status === ReservationStatus.Cancelled && "İptal"}
                            {reservation.status === ReservationStatus.Completed && "Tamamlandı"}
                          </Badge>
                        </TableCell>
                      </TableRow>
                    ))
                  )}
                </TableBody>
              </Table>
            </CardContent>
          </Card>
        </TabsContent>
      </Tabs>

      {/* Employee Dialog */}
      <Dialog open={employeeDialogOpen} onOpenChange={setEmployeeDialogOpen}>
        <DialogContent>
          <DialogHeader>
            <DialogTitle>{editingEmployee ? "Çalışanı Düzenle" : "Yeni Çalışan Ekle"}</DialogTitle>
            <DialogDescription>
              Çalışan bilgilerini girin
            </DialogDescription>
          </DialogHeader>
          <div className="space-y-4">
            <div>
              <Label htmlFor="fullName">Ad Soyad</Label>
              <Input
                id="fullName"
                value={employeeForm.fullName}
                onChange={(e) => setEmployeeForm({ ...employeeForm, fullName: e.target.value })}
              />
            </div>
            <div>
              <Label htmlFor="email">Email</Label>
              <Input
                id="email"
                type="email"
                value={employeeForm.email}
                onChange={(e) => setEmployeeForm({ ...employeeForm, email: e.target.value })}
              />
            </div>
            <div>
              <Label htmlFor="phoneNumber">Telefon</Label>
              <Input
                id="phoneNumber"
                value={employeeForm.phoneNumber}
                onChange={(e) => setEmployeeForm({ ...employeeForm, phoneNumber: e.target.value })}
              />
            </div>
            {!editingEmployee && (
              <div>
                <Label htmlFor="password">Şifre</Label>
                <Input
                  id="password"
                  type="password"
                  value={employeeForm.password}
                  onChange={(e) => setEmployeeForm({ ...employeeForm, password: e.target.value })}
                />
              </div>
            )}
          </div>
          <DialogFooter>
            <Button variant="outline" onClick={() => setEmployeeDialogOpen(false)}>
              İptal
            </Button>
            <Button onClick={handleSaveEmployee}>Kaydet</Button>
          </DialogFooter>
        </DialogContent>
      </Dialog>

      {/* Application Dialog */}
      <Dialog open={applicationDialogOpen} onOpenChange={setApplicationDialogOpen}>
        <DialogContent>
          <DialogHeader>
            <DialogTitle>Başvuru Detayları</DialogTitle>
            <DialogDescription>
              {selectedApplication?.applicantName} - {selectedApplication?.applicantEmail}
            </DialogDescription>
          </DialogHeader>
          {selectedApplication && (
            <div className="space-y-4">
              <div>
                <Label>Telefon</Label>
                <p className="text-sm">{selectedApplication.applicantPhone || "-"}</p>
              </div>
              <div>
                <Label>Başvuru Mektubu</Label>
                <p className="text-sm whitespace-pre-wrap">{selectedApplication.coverLetter}</p>
              </div>
              <div>
                <Label>Başvuru Tarihi</Label>
                <p className="text-sm">{new Date(selectedApplication.appliedDate).toLocaleDateString('tr-TR')}</p>
              </div>
              {selectedApplication.status === JobApplicationStatus.Pending && (
                <div>
                  <Label htmlFor="rejectionReason">Red Nedeni (Opsiyonel)</Label>
                  <Textarea
                    id="rejectionReason"
                    value={rejectionReason}
                    onChange={(e) => setRejectionReason(e.target.value)}
                    placeholder="Red nedeni belirtebilirsiniz..."
                  />
                </div>
              )}
            </div>
          )}
          <DialogFooter>
            {selectedApplication?.status === JobApplicationStatus.Pending && (
              <>
                <Button
                  variant="destructive"
                  onClick={() => handleRejectApplication(selectedApplication.id)}
                >
                  <XCircle className="h-4 w-4 mr-2" />
                  Reddet
                </Button>
                <Button onClick={() => handleAcceptApplication(selectedApplication.id)}>
                  <CheckCircle className="h-4 w-4 mr-2" />
                  Kabul Et
                </Button>
              </>
            )}
            <Button variant="outline" onClick={() => setApplicationDialogOpen(false)}>
              Kapat
            </Button>
          </DialogFooter>
        </DialogContent>
      </Dialog>

      {/* Review Response Dialog */}
      <Dialog open={reviewDialogOpen} onOpenChange={setReviewDialogOpen}>
        <DialogContent>
          <DialogHeader>
            <DialogTitle>Yoruma Yanıt Ver</DialogTitle>
            <DialogDescription>
              {selectedReview?.customerName} - {selectedReview?.rating} ⭐
            </DialogDescription>
          </DialogHeader>
          {selectedReview && (
            <div className="space-y-4">
              <div>
                <Label>Yorum</Label>
                <p className="text-sm whitespace-pre-wrap">{selectedReview.comment}</p>
              </div>
              <div>
                <Label htmlFor="reviewResponse">Yanıtınız</Label>
                <Textarea
                  id="reviewResponse"
                  value={reviewResponse}
                  onChange={(e) => setReviewResponse(e.target.value)}
                  placeholder="Müşteriye yanıt yazın..."
                  rows={4}
                />
              </div>
            </div>
          )}
          <DialogFooter>
            <Button variant="outline" onClick={() => setReviewDialogOpen(false)}>
              İptal
            </Button>
            <Button onClick={handleRespondToReview}>
              <MessageSquare className="h-4 w-4 mr-2" />
              Yanıt Gönder
            </Button>
          </DialogFooter>
        </DialogContent>
      </Dialog>
    </div>
  )
}

"use client"

import { useState, useEffect } from "react"
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card"
import { Button } from "@/components/ui/button"
import { Plus, Edit, Trash2, Mail, Phone } from "lucide-react"
import { useToast } from "@/hooks/use-toast"
import { useRestaurant } from "@/contexts/restaurant-context"
import { employeeApi } from "@/lib/api"
import {
  Dialog,
  DialogContent,
  DialogDescription,
  DialogFooter,
  DialogHeader,
  DialogTitle,
} from "@/components/ui/dialog"
import { Input } from "@/components/ui/input"
import { Label } from "@/components/ui/label"
import { AppUser } from "@/types"
import {
  Table,
  TableBody,
  TableCell,
  TableHead,
  TableHeader,
  TableRow,
} from "@/components/ui/table"
import {
  Avatar,
  AvatarFallback,
  AvatarImage,
} from "@/components/ui/avatar"

export default function OwnerEmployeesPage() {
  const { selectedRestaurant } = useRestaurant()
  const { toast } = useToast()

  const [employees, setEmployees] = useState<AppUser[]>([])
  const [loading, setLoading] = useState(true)

  const [employeeDialogOpen, setEmployeeDialogOpen] = useState(false)
  const [editingEmployee, setEditingEmployee] = useState<AppUser | null>(null)
  const [employeeForm, setEmployeeForm] = useState({
    fullName: "",
    email: "",
    phoneNumber: "",
    password: "",
    address: "",
  })

  useEffect(() => {
    if (selectedRestaurant) {
      loadEmployees()
    }
  }, [selectedRestaurant])

  const loadEmployees = async () => {
    if (!selectedRestaurant) return

    setLoading(true)
    try {
      const data = await employeeApi.getByRestaurant(selectedRestaurant.id)
      setEmployees(data)
    } catch (error: any) {
      toast({
        title: "Hata",
        description: error.message || "Çalışanlar yüklenirken hata oluştu",
        variant: "destructive",
      })
    } finally {
      setLoading(false)
    }
  }

  const handleSaveEmployee = async () => {
    if (!selectedRestaurant) return

    // Validasyon
    if (!employeeForm.fullName || !employeeForm.email) {
      toast({
        title: "Hata",
        description: "Lütfen zorunlu alanları doldurun",
        variant: "destructive",
      })
      return
    }

    if (!editingEmployee && !employeeForm.password) {
      toast({
        title: "Hata",
        description: "Şifre gereklidir",
        variant: "destructive",
      })
      return
    }

    try {
      const data = {
        ...employeeForm,
        ...(editingEmployee && !employeeForm.password && { password: undefined }),
      }

      if (editingEmployee) {
        await employeeApi.update(selectedRestaurant.id, editingEmployee.id, data)
        toast({
          title: "Başarılı",
          description: "Çalışan güncellendi",
        })
      } else {
        await employeeApi.create(selectedRestaurant.id, data)
        toast({
          title: "Başarılı",
          description: "Çalışan eklendi",
        })
      }

      setEmployeeDialogOpen(false)
      setEditingEmployee(null)
      setEmployeeForm({
        fullName: "",
        email: "",
        phoneNumber: "",
        password: "",
        address: "",
      })
      await loadEmployees()
    } catch (error: any) {
      toast({
        title: "Hata",
        description: error.message || "İşlem başarısız",
        variant: "destructive",
      })
    }
  }

  const handleDeleteEmployee = async (employeeId: string) => {
    if (!selectedRestaurant || !confirm("Bu çalışanı silmek istediğinizden emin misiniz?")) return

    try {
      await employeeApi.delete(selectedRestaurant.id, employeeId)
      toast({
        title: "Başarılı",
        description: "Çalışan silindi",
      })
      await loadEmployees()
    } catch (error: any) {
      toast({
        title: "Hata",
        description: error.message || "Silme işlemi başarısız",
        variant: "destructive",
      })
    }
  }

  const handleEditEmployee = (employee: AppUser) => {
    setEditingEmployee(employee)
    setEmployeeForm({
      fullName: employee.fullName,
      email: employee.email,
      phoneNumber: employee.phoneNumber || "",
      password: "",
      address: employee.address || "",
    })
    setEmployeeDialogOpen(true)
  }

  const getInitials = (name: string) => {
    return name
      .split(" ")
      .map((n) => n[0])
      .join("")
      .toUpperCase()
      .slice(0, 2)
  }

  if (!selectedRestaurant) {
    return (
      <div className="flex items-center justify-center min-h-screen">
        <Card className="w-96">
          <CardHeader>
            <CardTitle>Restoran Seçin</CardTitle>
            <CardDescription>
              Devam etmek için lütfen bir restoran seçin
            </CardDescription>
          </CardHeader>
        </Card>
      </div>
    )
  }

  if (loading) {
    return (
      <div className="flex items-center justify-center min-h-screen">
        <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-primary" />
      </div>
    )
  }

  return (
    <div className="container mx-auto p-6 space-y-6">
      <div className="flex items-center justify-between">
        <div>
          <h1 className="text-3xl font-bold">Çalışanlar</h1>
          <p className="text-muted-foreground">{selectedRestaurant.name}</p>
        </div>
        <Button onClick={() => {
          setEditingEmployee(null)
          setEmployeeForm({
            fullName: "",
            email: "",
            phoneNumber: "",
            password: "",
            address: "",
          })
          setEmployeeDialogOpen(true)
        }}>
          <Plus className="h-4 w-4 mr-2" />
          Yeni Çalışan
        </Button>
      </div>

      {/* Stats */}
      <Card>
        <CardHeader className="pb-2">
          <CardTitle className="text-sm font-medium">Toplam Çalışan</CardTitle>
        </CardHeader>
        <CardContent>
          <div className="text-2xl font-bold">{employees.length}</div>
          <p className="text-xs text-muted-foreground">
            Aktif personel sayısı
          </p>
        </CardContent>
      </Card>

      {/* Employees List */}
      <Card>
        <CardHeader>
          <CardTitle>Çalışan Listesi</CardTitle>
          <CardDescription>Restoranınızda çalışan personeli yönetin</CardDescription>
        </CardHeader>
        <CardContent>
          {employees.length === 0 ? (
            <div className="text-center py-12">
              <p className="text-muted-foreground mb-4">Henüz çalışan bulunmuyor</p>
              <Button onClick={() => {
                setEditingEmployee(null)
                setEmployeeForm({
                  fullName: "",
                  email: "",
                  phoneNumber: "",
                  password: "",
                  address: "",
                })
                setEmployeeDialogOpen(true)
              }}>
                <Plus className="h-4 w-4 mr-2" />
                İlk Çalışanı Ekle
              </Button>
            </div>
          ) : (
            <Table>
              <TableHeader>
                <TableRow>
                  <TableHead>Çalışan</TableHead>
                  <TableHead>İletişim</TableHead>
                  <TableHead>Adres</TableHead>
                  <TableHead>Kayıt Tarihi</TableHead>
                  <TableHead className="text-right">İşlemler</TableHead>
                </TableRow>
              </TableHeader>
              <TableBody>
                {employees.map((employee) => (
                  <TableRow key={employee.id}>
                    <TableCell>
                      <div className="flex items-center gap-3">
                        <Avatar>
                          <AvatarImage src={employee.profileImageUrl} />
                          <AvatarFallback>{getInitials(employee.fullName)}</AvatarFallback>
                        </Avatar>
                        <div>
                          <p className="font-medium">{employee.fullName}</p>
                          <p className="text-sm text-muted-foreground flex items-center gap-1">
                            <Mail className="h-3 w-3" />
                            {employee.email}
                          </p>
                        </div>
                      </div>
                    </TableCell>
                    <TableCell>
                      {employee.phoneNumber ? (
                        <div className="flex items-center gap-1 text-sm">
                          <Phone className="h-3 w-3" />
                          {employee.phoneNumber}
                        </div>
                      ) : (
                        <span className="text-muted-foreground">-</span>
                      )}
                    </TableCell>
                    <TableCell className="max-w-xs truncate">
                      {employee.address || <span className="text-muted-foreground">-</span>}
                    </TableCell>
                    <TableCell>
                      {new Date(employee.createdAt).toLocaleDateString('tr-TR')}
                    </TableCell>
                    <TableCell className="text-right">
                      <div className="flex justify-end gap-2">
                        <Button
                          variant="outline"
                          size="sm"
                          onClick={() => handleEditEmployee(employee)}
                        >
                          <Edit className="h-4 w-4" />
                        </Button>
                        <Button
                          variant="destructive"
                          size="sm"
                          onClick={() => handleDeleteEmployee(employee.id)}
                        >
                          <Trash2 className="h-4 w-4" />
                        </Button>
                      </div>
                    </TableCell>
                  </TableRow>
                ))}
              </TableBody>
            </Table>
          )}
        </CardContent>
      </Card>

      {/* Employee Dialog */}
      <Dialog open={employeeDialogOpen} onOpenChange={setEmployeeDialogOpen}>
        <DialogContent className="max-w-md">
          <DialogHeader>
            <DialogTitle>{editingEmployee ? "Çalışanı Düzenle" : "Yeni Çalışan Ekle"}</DialogTitle>
            <DialogDescription>
              Çalışan bilgilerini girin
            </DialogDescription>
          </DialogHeader>
          <div className="space-y-4">
            <div>
              <Label htmlFor="fullName">Ad Soyad *</Label>
              <Input
                id="fullName"
                value={employeeForm.fullName}
                onChange={(e) => setEmployeeForm({ ...employeeForm, fullName: e.target.value })}
                placeholder="Örn: Ahmet Yılmaz"
              />
            </div>
            <div>
              <Label htmlFor="email">Email *</Label>
              <Input
                id="email"
                type="email"
                value={employeeForm.email}
                onChange={(e) => setEmployeeForm({ ...employeeForm, email: e.target.value })}
                placeholder="ornek@email.com"
              />
            </div>
            <div>
              <Label htmlFor="phoneNumber">Telefon</Label>
              <Input
                id="phoneNumber"
                value={employeeForm.phoneNumber}
                onChange={(e) => setEmployeeForm({ ...employeeForm, phoneNumber: e.target.value })}
                placeholder="0555 123 45 67"
              />
            </div>
            <div>
              <Label htmlFor="address">Adres</Label>
              <Input
                id="address"
                value={employeeForm.address}
                onChange={(e) => setEmployeeForm({ ...employeeForm, address: e.target.value })}
                placeholder="Tam adres..."
              />
            </div>
            <div>
              <Label htmlFor="password">
                Şifre {editingEmployee ? "(Değiştirmek için doldurun)" : "*"}
              </Label>
              <Input
                id="password"
                type="password"
                value={employeeForm.password}
                onChange={(e) => setEmployeeForm({ ...employeeForm, password: e.target.value })}
                placeholder={editingEmployee ? "Boş bırakabilirsiniz" : "Güçlü şifre"}
              />
            </div>
          </div>
          <DialogFooter>
            <Button variant="outline" onClick={() => setEmployeeDialogOpen(false)}>
              İptal
            </Button>
            <Button onClick={handleSaveEmployee}>Kaydet</Button>
          </DialogFooter>
        </DialogContent>
      </Dialog>
    </div>
  )
}

"use client"

import { useState, useEffect } from "react"
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card"
import { Button } from "@/components/ui/button"
import { Badge } from "@/components/ui/badge"
import { Plus, Edit, Trash2 } from "lucide-react"
import { useToast } from "@/hooks/use-toast"
import { useRestaurant } from "@/contexts/restaurant-context"
import { menuApi } from "@/lib/api"
import {
  Dialog,
  DialogContent,
  DialogDescription,
  DialogFooter,
  DialogHeader,
  DialogTitle,
} from "@/components/ui/dialog"
import { Input } from "@/components/ui/input"
import { Label } from "@/components/ui/label"
import { Textarea } from "@/components/ui/textarea"
import { Menu, MenuItem } from "@/types"
import {
  Table,
  TableBody,
  TableCell,
  TableHead,
  TableHeader,
  TableRow,
} from "@/components/ui/table"
import { Switch } from "@/components/ui/switch"
import {
  Accordion,
  AccordionContent,
  AccordionItem,
  AccordionTrigger,
} from "@/components/ui/accordion"

export default function OwnerMenusPage() {
  const { selectedRestaurant } = useRestaurant()
  const { toast } = useToast()

  const [menus, setMenus] = useState<Menu[]>([])
  const [loading, setLoading] = useState(true)

  // Menu Dialog
  const [menuDialogOpen, setMenuDialogOpen] = useState(false)
  const [editingMenu, setEditingMenu] = useState<Menu | null>(null)
  const [menuForm, setMenuForm] = useState({
    name: "",
    description: "",
  })

  // Menu Item Dialog
  const [menuItemDialogOpen, setMenuItemDialogOpen] = useState(false)
  const [editingMenuItem, setEditingMenuItem] = useState<MenuItem | null>(null)
  const [selectedMenuId, setSelectedMenuId] = useState<string>("")
  const [menuItemForm, setMenuItemForm] = useState({
    name: "",
    description: "",
    price: 0,
    category: "",
    isAvailable: true,
    imageUrl: "",
  })

  useEffect(() => {
    if (selectedRestaurant) {
      loadMenus()
    }
  }, [selectedRestaurant])

  const loadMenus = async () => {
    if (!selectedRestaurant) return

    setLoading(true)
    try {
      const data = await menuApi.getByRestaurant(selectedRestaurant.id)
      setMenus(data)
    } catch (error: any) {
      toast({
        title: "Hata",
        description: error.message || "Menüler yüklenirken hata oluştu",
        variant: "destructive",
      })
    } finally {
      setLoading(false)
    }
  }

  // Menu CRUD operations
  const handleSaveMenu = async () => {
    if (!selectedRestaurant) return

    try {
      if (editingMenu) {
        await menuApi.update(editingMenu.id, {
          ...menuForm,
          restaurantId: selectedRestaurant.id,
        })
        toast({
          title: "Başarılı",
          description: "Menü güncellendi",
        })
      } else {
        await menuApi.create({
          ...menuForm,
          restaurantId: selectedRestaurant.id,
        })
        toast({
          title: "Başarılı",
          description: "Menü oluşturuldu",
        })
      }

      setMenuDialogOpen(false)
      setEditingMenu(null)
      setMenuForm({ name: "", description: "" })
      await loadMenus()
    } catch (error: any) {
      toast({
        title: "Hata",
        description: error.message || "İşlem başarısız",
        variant: "destructive",
      })
    }
  }

  const handleDeleteMenu = async (menuId: string) => {
    if (!confirm("Bu menüyü silmek istediğinizden emin misiniz?")) return

    try {
      await menuApi.delete(menuId)
      toast({
        title: "Başarılı",
        description: "Menü silindi",
      })
      await loadMenus()
    } catch (error: any) {
      toast({
        title: "Hata",
        description: error.message || "Silme işlemi başarısız",
        variant: "destructive",
      })
    }
  }

  const handleEditMenu = (menu: Menu) => {
    setEditingMenu(menu)
    setMenuForm({
      name: menu.name,
      description: menu.description,
    })
    setMenuDialogOpen(true)
  }

  // Menu Item CRUD operations
  const handleSaveMenuItem = async () => {
    if (!selectedMenuId) return

    try {
      const data = {
        ...menuItemForm,
        menuId: selectedMenuId,
      }

      if (editingMenuItem) {
        await menuApi.update(editingMenuItem.id, data)
        toast({
          title: "Başarılı",
          description: "Ürün güncellendi",
        })
      } else {
        await menuApi.create(data)
        toast({
          title: "Başarılı",
          description: "Ürün eklendi",
        })
      }

      setMenuItemDialogOpen(false)
      setEditingMenuItem(null)
      setMenuItemForm({
        name: "",
        description: "",
        price: 0,
        category: "",
        isAvailable: true,
        imageUrl: "",
      })
      await loadMenus()
    } catch (error: any) {
      toast({
        title: "Hata",
        description: error.message || "İşlem başarısız",
        variant: "destructive",
      })
    }
  }

  const handleDeleteMenuItem = async (menuItemId: string) => {
    if (!confirm("Bu ürünü silmek istediğinizden emin misiniz?")) return

    try {
      await menuApi.delete(menuItemId)
      toast({
        title: "Başarılı",
        description: "Ürün silindi",
      })
      await loadMenus()
    } catch (error: any) {
      toast({
        title: "Hata",
        description: error.message || "Silme işlemi başarısız",
        variant: "destructive",
      })
    }
  }

  const handleEditMenuItem = (menuId: string, menuItem: MenuItem) => {
    setSelectedMenuId(menuId)
    setEditingMenuItem(menuItem)
    setMenuItemForm({
      name: menuItem.name,
      description: menuItem.description,
      price: menuItem.price,
      category: menuItem.category || "",
      isAvailable: menuItem.isAvailable,
      imageUrl: menuItem.imageUrl || "",
    })
    setMenuItemDialogOpen(true)
  }

  const handleAddMenuItem = (menuId: string) => {
    setSelectedMenuId(menuId)
    setEditingMenuItem(null)
    setMenuItemForm({
      name: "",
      description: "",
      price: 0,
      category: "",
      isAvailable: true,
      imageUrl: "",
    })
    setMenuItemDialogOpen(true)
  }

  if (!selectedRestaurant) {
    return (
      <div className="flex items-center justify-center min-h-screen">
        <Card className="w-96">
          <CardHeader>
            <CardTitle>Restoran Seçin</CardTitle>
            <CardDescription>
              Devam etmek için lütfen bir restoran seçin
            </CardDescription>
          </CardHeader>
        </Card>
      </div>
    )
  }

  if (loading) {
    return (
      <div className="flex items-center justify-center min-h-screen">
        <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-primary" />
      </div>
    )
  }

  return (
    <div className="container mx-auto p-6 space-y-6">
      <div className="flex items-center justify-between">
        <div>
          <h1 className="text-3xl font-bold">Menüler</h1>
          <p className="text-muted-foreground">{selectedRestaurant.name}</p>
        </div>
        <Button onClick={() => {
          setEditingMenu(null)
          setMenuForm({ name: "", description: "" })
          setMenuDialogOpen(true)
        }}>
          <Plus className="h-4 w-4 mr-2" />
          Yeni Menü
        </Button>
      </div>

      {menus.length === 0 ? (
        <Card>
          <CardContent className="flex flex-col items-center justify-center py-12">
            <p className="text-muted-foreground mb-4">Henüz menü oluşturulmamış</p>
            <Button onClick={() => {
              setEditingMenu(null)
              setMenuForm({ name: "", description: "" })
              setMenuDialogOpen(true)
            }}>
              <Plus className="h-4 w-4 mr-2" />
              İlk Menüyü Oluştur
            </Button>
          </CardContent>
        </Card>
      ) : (
        <Accordion type="single" collapsible className="space-y-4">
          {menus.map((menu) => (
            <AccordionItem key={menu.id} value={menu.id}>
              <Card>
                <AccordionTrigger className="px-6 hover:no-underline">
                  <div className="flex items-center justify-between w-full pr-4">
                    <div className="text-left">
                      <h3 className="text-lg font-semibold">{menu.name}</h3>
                      <p className="text-sm text-muted-foreground">{menu.description}</p>
                    </div>
                    <div className="flex items-center gap-2">
                      <Badge variant="secondary">
                        {menu.menuItems?.length || 0} ürün
                      </Badge>
                      <Button
                        variant="outline"
                        size="sm"
                        onClick={(e) => {
                          e.stopPropagation()
                          handleEditMenu(menu)
                        }}
                      >
                        <Edit className="h-4 w-4" />
                      </Button>
                      <Button
                        variant="destructive"
                        size="sm"
                        onClick={(e) => {
                          e.stopPropagation()
                          handleDeleteMenu(menu.id)
                        }}
                      >
                        <Trash2 className="h-4 w-4" />
                      </Button>
                    </div>
                  </div>
                </AccordionTrigger>
                <AccordionContent>
                  <CardContent className="pt-4">
                    <div className="flex items-center justify-between mb-4">
                      <h4 className="font-medium">Menü Ürünleri</h4>
                      <Button
                        size="sm"
                        onClick={() => handleAddMenuItem(menu.id)}
                      >
                        <Plus className="h-4 w-4 mr-2" />
                        Ürün Ekle
                      </Button>
                    </div>

                    {(!menu.menuItems || menu.menuItems.length === 0) ? (
                      <div className="text-center py-8 text-muted-foreground">
                        <p>Bu menüde henüz ürün bulunmuyor</p>
                      </div>
                    ) : (
                      <Table>
                        <TableHeader>
                          <TableRow>
                            <TableHead>Ürün Adı</TableHead>
                            <TableHead>Kategori</TableHead>
                            <TableHead>Fiyat</TableHead>
                            <TableHead>Durumu</TableHead>
                            <TableHead className="text-right">İşlemler</TableHead>
                          </TableRow>
                        </TableHeader>
                        <TableBody>
                          {menu.menuItems.map((item) => (
                            <TableRow key={item.id}>
                              <TableCell>
                                <div>
                                  <p className="font-medium">{item.name}</p>
                                  <p className="text-sm text-muted-foreground">
                                    {item.description}
                                  </p>
                                </div>
                              </TableCell>
                              <TableCell>{item.category || "-"}</TableCell>
                              <TableCell className="font-medium">
                                ₺{item.price.toFixed(2)}
                              </TableCell>
                              <TableCell>
                                <Badge variant={item.isAvailable ? "default" : "secondary"}>
                                  {item.isAvailable ? "Mevcut" : "Mevcut Değil"}
                                </Badge>
                              </TableCell>
                              <TableCell className="text-right">
                                <div className="flex justify-end gap-2">
                                  <Button
                                    variant="outline"
                                    size="sm"
                                    onClick={() => handleEditMenuItem(menu.id, item)}
                                  >
                                    <Edit className="h-4 w-4" />
                                  </Button>
                                  <Button
                                    variant="destructive"
                                    size="sm"
                                    onClick={() => handleDeleteMenuItem(item.id)}
                                  >
                                    <Trash2 className="h-4 w-4" />
                                  </Button>
                                </div>
                              </TableCell>
                            </TableRow>
                          ))}
                        </TableBody>
                      </Table>
                    )}
                  </CardContent>
                </AccordionContent>
              </Card>
            </AccordionItem>
          ))}
        </Accordion>
      )}

      {/* Menu Dialog */}
      <Dialog open={menuDialogOpen} onOpenChange={setMenuDialogOpen}>
        <DialogContent>
          <DialogHeader>
            <DialogTitle>{editingMenu ? "Menüyü Düzenle" : "Yeni Menü Oluştur"}</DialogTitle>
            <DialogDescription>
              Menü bilgilerini girin
            </DialogDescription>
          </DialogHeader>
          <div className="space-y-4">
            <div>
              <Label htmlFor="menuName">Menü Adı</Label>
              <Input
                id="menuName"
                value={menuForm.name}
                onChange={(e) => setMenuForm({ ...menuForm, name: e.target.value })}
                placeholder="Örn: Ana Yemekler, Tatlılar..."
              />
            </div>
            <div>
              <Label htmlFor="menuDescription">Açıklama</Label>
              <Textarea
                id="menuDescription"
                value={menuForm.description}
                onChange={(e) => setMenuForm({ ...menuForm, description: e.target.value })}
                placeholder="Menü hakkında kısa açıklama..."
                rows={3}
              />
            </div>
          </div>
          <DialogFooter>
            <Button variant="outline" onClick={() => setMenuDialogOpen(false)}>
              İptal
            </Button>
            <Button onClick={handleSaveMenu}>Kaydet</Button>
          </DialogFooter>
        </DialogContent>
      </Dialog>

      {/* Menu Item Dialog */}
      <Dialog open={menuItemDialogOpen} onOpenChange={setMenuItemDialogOpen}>
        <DialogContent className="max-w-2xl">
          <DialogHeader>
            <DialogTitle>{editingMenuItem ? "Ürünü Düzenle" : "Yeni Ürün Ekle"}</DialogTitle>
            <DialogDescription>
              Ürün bilgilerini girin
            </DialogDescription>
          </DialogHeader>
          <div className="space-y-4">
            <div className="grid grid-cols-2 gap-4">
              <div>
                <Label htmlFor="itemName">Ürün Adı</Label>
                <Input
                  id="itemName"
                  value={menuItemForm.name}
                  onChange={(e) => setMenuItemForm({ ...menuItemForm, name: e.target.value })}
                  placeholder="Örn: İskender Kebap"
                />
              </div>
              <div>
                <Label htmlFor="itemCategory">Kategori</Label>
                <Input
                  id="itemCategory"
                  value={menuItemForm.category}
                  onChange={(e) => setMenuItemForm({ ...menuItemForm, category: e.target.value })}
                  placeholder="Örn: Kebaplar"
                />
              </div>
            </div>
            <div>
              <Label htmlFor="itemDescription">Açıklama</Label>
              <Textarea
                id="itemDescription"
                value={menuItemForm.description}
                onChange={(e) => setMenuItemForm({ ...menuItemForm, description: e.target.value })}
                placeholder="Ürün hakkında detaylı açıklama..."
                rows={3}
              />
            </div>
            <div className="grid grid-cols-2 gap-4">
              <div>
                <Label htmlFor="itemPrice">Fiyat (₺)</Label>
                <Input
                  id="itemPrice"
                  type="number"
                  step="0.01"
                  min="0"
                  value={menuItemForm.price}
                  onChange={(e) => setMenuItemForm({ ...menuItemForm, price: parseFloat(e.target.value) })}
                />
              </div>
              <div>
                <Label htmlFor="itemImageUrl">Resim URL (Opsiyonel)</Label>
                <Input
                  id="itemImageUrl"
                  value={menuItemForm.imageUrl}
                  onChange={(e) => setMenuItemForm({ ...menuItemForm, imageUrl: e.target.value })}
                  placeholder="https://..."
                />
              </div>
            </div>
            <div className="flex items-center space-x-2">
              <Switch
                id="itemAvailable"
                checked={menuItemForm.isAvailable}
                onCheckedChange={(checked) => setMenuItemForm({ ...menuItemForm, isAvailable: checked })}
              />
              <Label htmlFor="itemAvailable">Ürün mevcut</Label>
            </div>
          </div>
          <DialogFooter>
            <Button variant="outline" onClick={() => setMenuItemDialogOpen(false)}>
              İptal
            </Button>
            <Button onClick={handleSaveMenuItem}>Kaydet</Button>
          </DialogFooter>
        </DialogContent>
      </Dialog>
    </div>
  )
}

"use client"

import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select"
import { useRestaurant } from "@/contexts/restaurant-context"
import { Store } from "lucide-react"

export default function RestaurantSelector() {
  const { restaurants, selectedRestaurant, selectRestaurant, loading } = useRestaurant()

  if (loading) {
    return (
      <div className="flex items-center gap-2 text-sm text-muted-foreground">
        <Store className="h-4 w-4" />
        <span>Yükleniyor...</span>
      </div>
    )
  }

  if (restaurants.length === 0) {
    return (
      <div className="text-sm text-muted-foreground">
        Henüz restoranınız yok
      </div>
    )
  }

  return (
    <Select
      value={selectedRestaurant?.id || ""}
      onValueChange={(value) => {
        const restaurant = restaurants.find((r) => r.id === value)
        if (restaurant) selectRestaurant(restaurant)
      }}
    >
      <SelectTrigger className="w-full">
        <div className="flex items-center gap-2">
          <Store className="h-4 w-4" />
          <SelectValue placeholder="Restoran seçin" />
        </div>
      </SelectTrigger>
      <SelectContent>
        {restaurants.map((restaurant) => (
          <SelectItem key={restaurant.id} value={restaurant.id}>
            {restaurant.name}
          </SelectItem>
        ))}
      </SelectContent>
    </Select>
  )
}

"use client"

import { createContext, useContext, useState, useEffect, ReactNode } from "react"
import { Restaurant } from "@/types"
import { restaurantApi } from "@/lib/api"
import { useAuth } from "./auth-context"

interface RestaurantContextType {
  restaurants: Restaurant[]
  selectedRestaurant: Restaurant | null
  selectRestaurant: (restaurant: Restaurant) => void
  loading: boolean
  refreshRestaurants: () => Promise<void>
}

const RestaurantContext = createContext<RestaurantContextType | undefined>(undefined)

export function RestaurantProvider({ children }: { children: ReactNode }) {
  const [restaurants, setRestaurants] = useState<Restaurant[]>([])
  const [selectedRestaurant, setSelectedRestaurant] = useState<Restaurant | null>(null)
  const [loading, setLoading] = useState(true)
  const { user } = useAuth()

  const fetchRestaurants = async () => {
    if (!user) return

    try {
      setLoading(true)
      const data = await restaurantApi.getAll()
      // Owner'a ait restoranları filtrele
      const ownerRestaurants = data.filter((r: Restaurant) => r.ownerId === user.id)
      setRestaurants(ownerRestaurants)

      // Eğer seçili restoran yoksa ve restoranlar varsa, ilkini seç
      if (!selectedRestaurant && ownerRestaurants.length > 0) {
        setSelectedRestaurant(ownerRestaurants[0])
        localStorage.setItem("selectedRestaurantId", ownerRestaurants[0].id)
      }

      // Local storage'dan seçili restoranı yükle
      const savedRestaurantId = localStorage.getItem("selectedRestaurantId")
      if (savedRestaurantId) {
        const restaurant = ownerRestaurants.find((r: Restaurant) => r.id === savedRestaurantId)
        if (restaurant) {
          setSelectedRestaurant(restaurant)
        }
      }
    } catch (error) {
      console.error("Error fetching restaurants:", error)
    } finally {
      setLoading(false)
    }
  }

  useEffect(() => {
    if (user) {
      fetchRestaurants()
    }
  }, [user])

  const selectRestaurant = (restaurant: Restaurant) => {
    setSelectedRestaurant(restaurant)
    localStorage.setItem("selectedRestaurantId", restaurant.id)
  }

  const refreshRestaurants = async () => {
    await fetchRestaurants()
  }

  return (
    <RestaurantContext.Provider
      value={{
        restaurants,
        selectedRestaurant,
        selectRestaurant,
        loading,
        refreshRestaurants,
      }}
    >
      {children}
    </RestaurantContext.Provider>
  )
}

export function useRestaurant() {
  const context = useContext(RestaurantContext)
  if (context === undefined) {
    throw new Error("useRestaurant must be used within a RestaurantProvider")
  }
  return context
}

import { AuthService } from "./auth"

const API_BASE_URL = process.env.NEXT_PUBLIC_API_URL || "http://localhost:5000/api"

export class ApiClient {
  private static getHeaders(): HeadersInit {
    const token = AuthService.getToken()
    return {
      "Content-Type": "application/json",
      ...(token && { Authorization: `Bearer ${token}` }),
    }
  }

  private static async handleResponse<T>(response: Response): Promise<T> {
    if (!response.ok) {
      const errorData = await response.json().catch(() => ({ message: response.statusText }))

      // Backend ModelState hatalarını handle et
      if (errorData.errors) {
        const errorMessages = Object.entries(errorData.errors)
          .map(([field, messages]: [string, any]) => {
            const msgArray = Array.isArray(messages) ? messages : [messages]
            return `${field}: ${msgArray.join(", ")}`
          })
          .join("\n")
        throw new Error(errorMessages)
      }

      throw new Error(errorData.message || errorData.Message || `API Error: ${response.statusText}`)
    }

    // 204 No Content durumu için boş obje döndür
    if (response.status === 204) {
      return {} as T
    }

    return response.json()
  }

  static async get<T>(endpoint: string): Promise<T> {
    const response = await fetch(`${API_BASE_URL}${endpoint}`, {
      method: "GET",
      headers: this.getHeaders(),
    })

    return this.handleResponse<T>(response)
  }

  static async post<T>(endpoint: string, data: unknown): Promise<T> {
    const response = await fetch(`${API_BASE_URL}${endpoint}`, {
      method: "POST",
      headers: this.getHeaders(),
      body: JSON.stringify(data),
    })

    return this.handleResponse<T>(response)
  }

  static async put<T>(endpoint: string, data: unknown): Promise<T> {
    const response = await fetch(`${API_BASE_URL}${endpoint}`, {
      method: "PUT",
      headers: this.getHeaders(),
      body: JSON.stringify(data),
    })

    return this.handleResponse<T>(response)
  }

  static async patch<T>(endpoint: string, data: unknown): Promise<T> {
    const response = await fetch(`${API_BASE_URL}${endpoint}`, {
      method: "PATCH",
      headers: this.getHeaders(),
      body: JSON.stringify(data),
    })

    return this.handleResponse<T>(response)
  }

  static async delete<T>(endpoint: string): Promise<T> {
    const response = await fetch(`${API_BASE_URL}${endpoint}`, {
      method: "DELETE",
      headers: this.getHeaders(),
    })

    return this.handleResponse<T>(response)
  }
}

// Restaurant API
export const restaurantApi = {
  getAll: () => ApiClient.get<any[]>("/Restaurant"),
  getById: (id: string) => ApiClient.get<any>(`/Restaurant/${id}`),
  create: (data: any) => ApiClient.post<any>("/Restaurant", data),
  update: (id: string, data: any) => ApiClient.put<any>(`/Restaurant/${id}`, data),
  delete: (id: string) => ApiClient.delete<any>(`/Restaurant/${id}`),
}

// Menu API
export const menuApi = {
  getByRestaurant: (restaurantId: string) => ApiClient.get<any[]>(`/Menu/restaurant/${restaurantId}`),
  getById: (id: string) => ApiClient.get<any>(`/Menu/${id}`),
  create: (data: any) => ApiClient.post<any>("/Menu", data),
  update: (id: string, data: any) => ApiClient.put<any>(`/Menu/${id}`, data),
  delete: (id: string) => ApiClient.delete<any>(`/Menu/${id}`),
}

// Order API
export const orderApi = {
  getAll: () => ApiClient.get<any[]>("/Order"),
  getById: (id: string) => ApiClient.get<any>(`/Order/${id}`),
  getByCustomer: (customerId: string) => ApiClient.get<any[]>(`/Order/customer/${customerId}`),
  create: (data: any) => ApiClient.post<any>("/Order", data),
  update: (id: string, data: any) => ApiClient.put<any>(`/Order/${id}`, data),
  updateStatus: (id: string, status: string) => ApiClient.patch<any>(`/Order/${id}/status`, { status }),
}

// Reservation API
export const reservationApi = {
  getAll: () => ApiClient.get<any[]>("/Reservation"),
  getById: (id: string) => ApiClient.get<any>(`/Reservation/${id}`),
  getByCustomer: (customerId: string) => ApiClient.get<any[]>(`/Reservation/customer/${customerId}`),
  create: (data: any) => ApiClient.post<any>("/Reservation", data),
  update: (id: string, data: any) => ApiClient.put<any>(`/Reservation/${id}`, data),
  cancel: (id: string) => ApiClient.delete<any>(`/Reservation/${id}`),
}

// Generic API client export for direct usage
export const api = ApiClient

import { AuthService } from "./auth"

const API_BASE_URL = process.env.NEXT_PUBLIC_API_URL || "http://localhost:5000/api"

export class ApiClient {
  private static getHeaders(): HeadersInit {
    const token = AuthService.getToken()
    return {
      "Content-Type": "application/json",
      ...(token && { Authorization: `Bearer ${token}` }),
    }
  }

  private static async handleResponse<T>(response: Response): Promise<T> {
    if (!response.ok) {
      const errorData = await response.json().catch(() => ({ message: response.statusText }))

      // Backend ModelState hatalarını handle et
      if (errorData.errors) {
        const errorMessages = Object.entries(errorData.errors)
          .map(([field, messages]: [string, any]) => {
            const msgArray = Array.isArray(messages) ? messages : [messages]
            return `${field}: ${msgArray.join(", ")}`
          })
          .join("\n")
        throw new Error(errorMessages)
      }

      throw new Error(errorData.message || errorData.Message || `API Error: ${response.statusText}`)
    }

    // 204 No Content durumu için boş obje döndür
    if (response.status === 204) {
      return {} as T
    }

    return response.json()
  }

  static async get<T>(endpoint: string): Promise<T> {
    const response = await fetch(`${API_BASE_URL}${endpoint}`, {
      method: "GET",
      headers: this.getHeaders(),
    })

    return this.handleResponse<T>(response)
  }

  static async post<T>(endpoint: string, data: unknown): Promise<T> {
    const response = await fetch(`${API_BASE_URL}${endpoint}`, {
      method: "POST",
      headers: this.getHeaders(),
      body: JSON.stringify(data),
    })

    return this.handleResponse<T>(response)
  }

  static async put<T>(endpoint: string, data: unknown): Promise<T> {
    const response = await fetch(`${API_BASE_URL}${endpoint}`, {
      method: "PUT",
      headers: this.getHeaders(),
      body: JSON.stringify(data),
    })

    return this.handleResponse<T>(response)
  }

  static async patch<T>(endpoint: string, data: unknown): Promise<T> {
    const response = await fetch(`${API_BASE_URL}${endpoint}`, {
      method: "PATCH",
      headers: this.getHeaders(),
      body: JSON.stringify(data),
    })

    return this.handleResponse<T>(response)
  }

  static async delete<T>(endpoint: string): Promise<T> {
    const response = await fetch(`${API_BASE_URL}${endpoint}`, {
      method: "DELETE",
      headers: this.getHeaders(),
    })

    return this.handleResponse<T>(response)
  }
}

// Restaurant API
export const restaurantApi = {
  getAll: () => ApiClient.get<any[]>("/Restaurant"),
  getById: (id: string) => ApiClient.get<any>(`/Restaurant/${id}`),
  create: (data: any) => ApiClient.post<any>("/Restaurant", data),
  update: (id: string, data: any) => ApiClient.put<any>(`/Restaurant/${id}`, data),
  delete: (id: string) => ApiClient.delete<any>(`/Restaurant/${id}`),
}

// Menu API
export const menuApi = {
  getByRestaurant: (restaurantId: string) => ApiClient.get<any[]>(`/Menu/restaurant/${restaurantId}`),
  getById: (id: string) => ApiClient.get<any>(`/Menu/${id}`),
  create: (data: any) => ApiClient.post<any>("/Menu", data),
  update: (id: string, data: any) => ApiClient.put<any>(`/Menu/${id}`, data),
  delete: (id: string) => ApiClient.delete<any>(`/Menu/${id}`),
}

// Order API
export const orderApi = {
  getAll: () => ApiClient.get<any[]>("/Order"),
  getById: (id: string) => ApiClient.get<any>(`/Order/${id}`),
  getByCustomer: (customerId: string) => ApiClient.get<any[]>(`/Order/customer/${customerId}`),
  create: (data: any) => ApiClient.post<any>("/Order", data),
  update: (id: string, data: any) => ApiClient.put<any>(`/Order/${id}`, data),
  updateStatus: (id: string, status: string) => ApiClient.patch<any>(`/Order/${id}/status`, { status }),
}

// Reservation API
