export interface Review extends BaseEntity { restaurantId: string customerId: string customerName: string rating: number comment: string status: ReviewStatus adminResponse?: string }
export enum ReviewStatus { Pending = "Pending", Approved = "Approved", Rejected = "Rejected", }

